void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;    
    vec3 blue = vec3(0.455,0.537,0.753);
    vec3 orange = vec3(0.961,0.318,0.035);
    vec3 offWhite = vec3(0.941,0.918,0.776);

    vec3 verGrad = mix(orange, blue, uv.y);
    vec3 horGrad = mix(offWhite, blue, uv.x);
    vec3 mixGrad = mix(mix(offWhite,blue, .1 + uv.x * sin(iTime) + uv.y * cos(iTime)), mix(verGrad, horGrad, -abs(sin(iTime + uv.x) * .5)), uv.x * uv.y);    

    vec3 color = vec3(0.0);
    uv *= 10.;
    vec2 pos = vec2(uv);

    // Use the noise function
    float n = snoise(vec3(pos.x, pos.y, iTime * .5));
    uv *= n;
    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);

    float m_dist = 1.; 

    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x),float(y));

            // Random position from current + neighbor place in the grid
            vec2 point = vec2(random2(i_uv + neighbor));

			// Animate the point
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);

			// Vector between the pixel and the point
            vec2 diff = neighbor + point - f_uv;

            // Distance to the point
            float dist = length(diff);

            // Keep the closer distance
            m_dist = min(m_dist, dist);
        }
    }

    // Draw the min distance (distance field)
    color += m_dist;

    // Show isolines
    //color -= step(.7,abs(sin(50.0*m_dist)))*.3;

    // Output to screen    

    color -= vec3(smoothstep(.7,abs(sin(27.0*m_dist)), .1)*.5, 1.0, 1.0);
    color += mixGrad;
    fragColor = vec4(color, 1.0);
}

