const vec2  v01 = vec2(0.0, 1.0);
const vec3  vX  = v01.yxx;
const vec3  vY  = v01.xyx;
const vec3  vZ  = v01.xxy;
const vec3  v0  = v01.xxx;
const vec3  v1  = v01.yyy;
const vec4  v40 = v01.xxxx;
const vec4  v41 = v01.yyyy;
const float pi  = 3.14159265359;

//---------------------------------------------------
// Screen-to-View stuff

#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))
#define ZERO   (min(iFrame,0))

#define VIEWFROMSCREEN_DIST(distance)                     \
        ( (distance) / MINRES / gZoom                     )
#define VIEWFROMSCREEN(xy)                                \
        ( vec2(VIEWFROMSCREEN_DIST((xy).x * 2.0 - RES.x), \
               VIEWFROMSCREEN_DIST((xy).y * 2.0 - RES.y)) )


//---------------------------------------------------
// math conveniences
#define saturate(v) ( clamp((v), 0.0, 1.0) )
float lengthSqr(vec3 a) { return dot(a, a); }
float lengthSqr(vec2 a) { return dot(a, a); }
float sqr(float a) { return a * a; }

mat2 rot2(in float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(c, s, -s, c);
}

int   vmax(ivec2 A) { return max(    A.x, A.y); }
float vmax( vec2 A) { return max(    A.x, A.y); }
float vmax( vec3 A) { return max(max(A.x, A.y), A.z); }
int   vmin(ivec2 A) { return min(    A.x, A.y); }
float vmin( vec2 A) { return min(    A.x, A.y); }
float vmin( vec3 A) { return min(min(A.x, A.y), A.z); }
float sat (float A) { return clamp(A,      0.0 ,      1.0 ); }
vec2  sat ( vec2 A) { return clamp(A, vec2(0.0), vec2(1.0)); }
vec3  sat ( vec3 A) { return clamp(A, vec3(0.0), vec3(1.0)); }
#define V2(A, B   )    ( vec2(A, B      ) )
#define V3(A, B, C)    ( vec3(A, B, C   ) )
#define V4(A, B, C, D) ( vec4(A, B, C, D) )

//--------------------------------------------------------------------------
// keyboard conveniences
//
#define KEY_IS_DOWN(key, chan) (texelFetch(chan, ivec2(key, 0), 0).r > 0.0)
#define KEY_TOGGLED(key, chan) (texelFetch(chan, ivec2(key, 2), 0).r > 0.0)


//--------------------------------------------------------------------------
// ray stuff
//
struct ray_t {
    vec3  ro;
    vec3  rd;
    vec3  amt;   // [0, 1] amount of light left in the ray

    // if true, this is a ray looking for the light.
    bool  isLightingRay;
};

struct mapSample_t {
    float dist;
    uint  mtl;
};
#define MST mapSample_t

struct hit_t {
    bool  hit;
    float t;
    vec3  pnt;
    vec3  hf_nrm;
    vec3  of_nrm;
    MST   ms;
};

struct mtl_t {
    vec3  albedo;
    float opacity;         // opacity at the surface
    float ior;
    vec4  absorption;      // <vec3, permeability>
};

//--------------------------------------------------------------------------
// optics

// A modified Schlick's approximation
// where reflectamount -> 0 as n2 -> n1.
float reflectAmount(float n1, float n2, float incidentDotNormal) {
    // from https://en.wikipedia.org/wiki/Schlick%27s_approximation 
    float R0 = sqr((n1 - n2) / (n1 + n2));
    float R  = R0 + (1.0 - R0) * pow(1.0 - incidentDotNormal, 5.0);

    // Schlick's approximation seems invalid when n1 ~= n2.
    // Compensate with an additional fudge.
    // https://physics.stackexchange.com/q/794963
    float ndiffSqr      = sqr(n1 - n2);
    float elenzilFudge  = ndiffSqr / (ndiffSqr + 0.01);
    R                  *= elenzilFudge;

    return R;
}

// n1 == n2          -> no reflection
void thisRayWalksIntoAnInterface(
    //  direction of the incoming ray.
    in  vec3  incident,

    //  surface normal.
    in  vec3  hit_facing_normal,

    //  indices of refraction.
    in  float n1,
    in  float n2,

    in  float opacity,

    //  amount allocated to diffuse lighting
    out float out_sDiffuse,

    //  direction and amount for specular reflection
    out vec4  out_vReflect,

    //  direction and amount for transmission
    out vec4  out_vTransmit
    ) {

    // 0 = transmit 1 = reflect 1
    float transmitVsReflect = reflectAmount(n1, n2, dot(incident, -hit_facing_normal));

    // check for total internal reflection.
    float eta     = n1 / n2;
    out_vTransmit.xyz = refract(incident, hit_facing_normal, eta);
    if (dot(out_vTransmit.xyz, out_vTransmit.xyz) == 0.0) {
        // total internal
        transmitVsReflect = 1.0;
    }

    float amtDiffuse  = (1.0 - transmitVsReflect) * opacity;
    float amtReflect  = transmitVsReflect;
    float amtTransmit =  1.0 - amtDiffuse - amtReflect;

    out_sDiffuse      = amtDiffuse;
    out_vTransmit.w   = amtTransmit;
    out_vReflect.w    = amtReflect;
    out_vReflect.xyz  = reflect(incident, hit_facing_normal);
}

