// mouse handling.
// outputs non-normalized mouse as (0,0).xy.
// defaults to iResolution.xy / 2.
// resets when mouse is in lower-left.
// https://www.shadertoy.com/view/Mf2GDy
// orion elenzil January 2024.

// Wrapping mode for X and Y:
// 0u = unconstrained
// 1u = clamp
// 2u = wrap
const uvec2 wrap = uvec2(0u, 1u);

// initial position (normalized)
const vec2  p0   = vec2(0.5, 0.75);

// when actual mouse is within this radius
// in the lower-right, reset to p0.
const float resetCircle = 75.0;

// 1 to visualize the mouse position and reset circle.
#define VISUALIZE 0

// special texels
const ivec2 pos = ivec2(0, 0);
const ivec2 btn = ivec2(1, 0);
const ivec2 hrt = ivec2(3, 0);


// data[0] <- bool mouseIsDown
// data[1] <- bool mouseJustBecameDown
// data.zw <- current mouse pos
void trackButton(inout vec4 data) {
    bool mouseIsDown         = iMouse.z > 0.0;
    bool mouseWasDown        = bool(data[0]);
    bool mouseJustBecameDown = !mouseWasDown && mouseIsDown;

    data[0] = float(mouseIsDown);
    data[1] = float(mouseJustBecameDown);
    data.zw = iMouse.xy;
}

// data.xy <- cumulative mousePos
// data.zw <- raw last mousePos
void trackMouse(inout vec4 data, in vec4 button) {
    bool mouseIsDown = bool(button[0]);
    bool mouseJustBecameDown = bool(button[1]);
    vec2 mouse = button.zw;

    vec2 home = p0 * iResolution.xy + 8.2 * iTime * v01.yx;

    if (iFrame == 0) {
        data.xy = home;
        data.zw = mouse;
        return;
    }

    if (dot(iMouse.xy, iMouse.xy) < resetCircle * resetCircle) {
        data.xy = mix(data.xy, home, 0.1);
        data.zw = mouse;
        return;
    }

    if (!mouseIsDown) {
        return;
    }

    if (mouseJustBecameDown) {
        data.zw = mouse;
    }

    vec2 prevMouse = data.zw;
    vec2 deltMouse = mouse - prevMouse;

    data.xy += deltMouse;

    for (int n = 0; n < 2; ++n) {
        switch (wrap[n]) {
            case 0u:
                break;
            case 1u:
                // clamp
                data[n] = clamp(data[n], 0.0, iResolution[n] - 1.0);
                break;
            case 2u:
                // wrap
                data[n] = mod(data[n], iResolution[n]);
                break;
        }
    }

    data.zw = mouse;
}

// buffers are currently 100% broken on all browsers on iOS.
// this provides a work-around so shaders can detect that.
void trackHeartbeat(inout vec4 RGBA) {
    RGBA.r = 1.0;
}

void mainImage(out vec4 RGBA, in vec2 XY) {
    ivec2 IJ = ivec2(XY);

    RGBA = texelFetch(iChannel2, IJ, 0);

    if (iFrame == 0) {
        RGBA = vec4(0);
    }

    if (IJ == btn) {
        trackButton(RGBA);
        return;
    }

    vec4 button = texelFetch(iChannel2, btn, 0);

    if (IJ == pos) {
        trackMouse(RGBA, button);
        return;
    }

    if (IJ == hrt) {
        trackHeartbeat(RGBA);
        return;
    }


#if VISUALIZE    
    vec4 mouse = texelFetch(iChannel2, pos, 0);

    vec2  p = XY;
    float d = 1e9;
    d = min(d, abs(length(p) - resetCircle));
    float a = atan(p.y, p.x) * 20.0 - iTime;
    d += smoothstep(-0.2, 0.2, sin(a));
    d = min(d, abs(length(p - mouse.xy) - 20.0) - 2.0);
    d = min(d, abs(length(p - mouse.zw) - 10.0) - 2.0);
    float c = smoothstep(2.0, 0.0, d);

    RGBA.rgb = vec3(c);
#else
    discard;
#endif
}

