    )
    {
        p.xyz=g*d2*col;
      p.z+=iTime*10.;
        a=30.;
        p=mod(p-a,a*2.)-a;
        s=2.;
        for(int i=0;i++<8;){
            p=.3-abs(p);
            p.z<p.w?p=p.zyxw:p;
            p.z<p.y?p=p.xzyw:p;
                    p.z<p.y?p=p.xzyw:p;
            s*=e=1.4+sin(iTime*.1)*.1;
            p.xyz=abs(p.xyz)*e-
                vec3(
