if (bit(n,  5)) v = min(v, dseg(vec2(-0.438, -1.000), vec2(-0.063, -1.000)));
if (bit(n,  6)) v = min(v, dseg(vec2( 0.063*sin(iTime), -1.000), vec2( 0.438, -1.000)));
if (bit(n,  7)) v = min(v, dseg(vec2( 0.500, -0.938), vec2( 0.500*cos(iTime), -0.063)));
if (bit(n,  8)) v = min(v, dseg(vec2( 0.063*cos(iTime),  0.000*sin(iTime)), vec2( 0.438, -0.000)));
if (bit(n,  9)) v = min(v, dseg(vec2( 0.063,  0.063), vec2( 0.438,  0.938)));
if (bit(n, 10)) v = min(v, dseg(vec2( 0.000*cos(iTime),  0.063*cos(iTime)), vec2( 0.000,  0.937)));
if (bit(n, 11)) v = min(v, dseg(vec2(-0.063,  0.063), vec2(-0.438,  0.938)));
if (bit(n, 12)) v = min(v, dseg(vec2(-0.438+c.x*cos(iTime),  0.000), vec2(-0.063, -0.000)*c.yz));
if (bit(n, 13)) v = min(v, dseg(vec2(-0.063*sin(iTime), -0.063)*c.yz, vec2(-0.438+c.x*sin(iTime), -0.938)*c.yz));
if (bit(n, 14)) v = min(v, dseg(vec2( 0.000*cos(iTime), -0.938*cos(iTime))*c.yz, vec2( 0.000, -0.063)*c.yz));
if (bit(n, 15)) v = min(v, dseg(vec2( 0.063, -0.063+c.x*sin(iTime)), vec2( 0.438+c.x*sin(iTime), -0.938)));
ch_pos.x += ch_space.x+0.1*c.x*cos(iTime);

d = min(d, v);
}
mat2 rotate(float a)
{
float c = cos(a);
float s = sin(a);
return mat2(c, s, -s, c);
}
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing

return c.z * mix( vec3(1.0), rgb, c.y);
}
