#define PI 3.14159265358979323846
#define iTime (iTime + sin(iTime*PI*2. - PI)/(2.*PI))

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

float circle(in vec2 _st, in float _radius){
    vec2 l = _st-vec2(0.5);
    return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(l,l)*4.0);
}

vec4 tile(vec2 _st, float _zoom){
    // returns space (xy) and column row info (zw)
    _st *= _zoom;
    float col = floor(_st.x);
    float row = floor(_st.y);   
    if (mod(row, 2.0) == 0.0 && mod(floor(iTime), 3.0) == 1.0) {
        _st.x += mod(iTime, 2.0);
    } else if (mod(col, 2.0) == 0.0 && mod(floor(iTime), 3.0) != 1.0) {
        _st.y += mod(iTime, 2.0);
    }
    return vec4(fract(_st), int(floor(_st.x)), int(floor(_st.y)));
}

float box(vec2 _st, vec2 _size, float _smoothEdges){
    _size = vec2(0.5)-_size*0.5;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size,_size+aa,_st);
    uv *= smoothstep(_size,_size+aa,vec2(1.0)-_st);
    return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    // Define gradient colors
    vec3 blue = vec3(0.455,0.537,0.753);
    vec3 orange = vec3(0.961,0.318,0.035);
    vec3 offWhite = vec3(0.941,0.918,0.776);

    // Get the grid and local coordinates within each cell
    vec4 grid = tile(uv, 10.0);
    vec2 localUV = grid.xy;

    // Calculate the gradient for the local coordinates
    vec3 verGrad = mix(orange, blue, localUV.y);
    vec3 horGrad = mix(offWhite, blue, localUV.x);
    vec3 mixGrad = mix(mix(offWhite, blue, .1 + localUV.x * sin(iTime) + localUV.y * cos(iTime)), 
                       mix(verGrad, horGrad, -abs(sin(iTime + localUV.x) * .5)), uv.x * uv.y);

    // Apply the gradient to the circles
    color = mixGrad * box(localUV, vec2(0.9, .9), .025);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
