float sdCircle(vec2 p, float radius) {
    return length(p - 0.5) - radius;
}

float plot(vec2 st, float fx, float border){
    return smoothstep( fx-border*0.5, fx, st.y) - smoothstep( fx, fx+border*0.5, st.y);
}

void drawGrid(vec2 st, float border, inout vec3 col) {
    col *= 1.0 - vec3(plot(st, 0.0, border*0.5));
    col *= 1.0 - vec3(plot(st, 1.0, border*0.5));

    col *= 1.0 - vec3(plot(st.yx, 0.0, border*0.5));
    col *= 1.0 - vec3(plot(st.yx, 1.0, border*0.5));
}

float distanceToSegment( vec2 a, vec2 b, vec2 p )
{
	vec2 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

float bresenhamLow(vec2 s, vec2 e, vec2 px){
    // Function needs delta.x >= 0

    vec2 delta = e - s;

    float incr = 1.0;

    if (delta.y < 0.0) {
        delta.y = -delta.y;
        incr = -1.0;
    }

    float d = delta.x - 2.0*delta.y;
    float y = s.y;

    for(float x = s.x; x <= e.x; x++){
        if (length(px - vec2(x, y)) < 0.00001){
            // Pixel = Bresenham line pixel
            return 1.0;
        }
        if (d > 0.0){
            d -= 2.0 * delta.y;
        }
        else{
            y += incr;
            d-= 2.0 * (delta.y - delta.x);
        }
    }

    return 0.0;
}

float bresenham(vec2 s, vec2 e, vec2 px) {    
    vec2 delta = abs(e - s);

    if (delta.y > delta.x) {
        // Slope > 1.
        s = s.yx;
        e = e.yx;
        px = px.yx;
    }

    // Makes sure delta.x >= 0.
    if (e.x >= s.x) {
        return bresenhamLow(s, e, px);
    }
    else {
        return bresenhamLow(e, s, px);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scale between 0 and 1.
    vec2 uv = fragCoord / iResolution.xy;
    vec2 ms = iMouse.xy / iResolution.xy; // Current mouse position.
    vec2 lms = abs(iMouse.zw) / iResolution.xy; // Last mouse click position.

    // Adjust for aspect ratio
    float aspectRatio = iResolution.x / iResolution.y;
    uv.x *= aspectRatio;
    ms.x *= aspectRatio;
    lms.x *= aspectRatio;

    // Adjust this value to increase pixels in the grid.
    float pxRes = 5.0;

    // Scale the space for tiling.
    uv *= pxRes;
    ms *= pxRes;
    lms *= pxRes;

    // Get start, end and pixel positions for bresenham.
    vec2 a = floor(lms) + 0.5;
    vec2 b = floor(ms) + 0.5;
    vec2 px = floor(uv) + 0.5;

    // Mask telling whether pixel belongs to bresenham line.
    float bpx = bresenham(a, b, px);

    vec3 col = vec3(1.0);

    // Tile the space.
    vec2 st = fract(uv);

    // Draw dot.
    float dotRadius = 0.1;
    float distDot = sdCircle(fract(st), dotRadius);
    col *= vec3(smoothstep(0.0, 0.02, distDot));

    float borderResFactor = 397320.0 / (iResolution.x * iResolution.y);
    float gridBorder = 0.015 * pxRes * borderResFactor;
    float subGridBorder = 0.007 * pxRes * borderResFactor;

    // Draw main grid.
    drawGrid(st, max(0.006 * pxRes, min(0.018 * pxRes, gridBorder)), col);
    // Draw sub-grid.
    drawGrid(st - 0.5, max(0.007 * pxRes, min(0.014 * pxRes, subGridBorder)), col);

    // Highlight bresenham pixel.
    col = mix(col, vec3(1.0 - bpx), 0.5 * bpx);

    // Draw line
    vec3 lineColor = vec3(1.0, 1.0,0.0);
    float lineTransparency = 0.9;
    float lineRoundness = dotRadius * 0.01 * pxRes;
    float d = distanceToSegment(a, b, uv) - lineRoundness;
    col = mix( col, mix(col, lineColor, lineTransparency), 1.0-smoothstep(.01,0.01 + pxRes*0.002, d) );

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define FXAA

// Credits: https://www.shadertoy.com/view/mtScRc
vec4 fxaa(sampler2D tex, vec2 uv, vec2 texelSz)
{
    // Maximum texel span
    const float span_max = 8.0;
    // These are more technnical and probably don't need changing:
    // Minimum "dir" reciprocal
    const float reduce_min = (1.0/128.0);
    // Luma multiplier for "dir" reciprocal
    const float reduce_mul = (1.0/32.0);

    const vec3 luma = vec3(0.299, 0.587, 0.114);

    // Sample center and 4 corners
    vec3 rgbCC = texture(tex, uv).rgb;
    vec3 rgb00 = texture(tex, uv + vec2(-0.5, -0.5) * texelSz).rgb;
    vec3 rgb10 = texture(tex, uv + vec2(+0.5, -0.5) * texelSz).rgb;
    vec3 rgb01 = texture(tex, uv + vec2(-0.5, +0.5) * texelSz).rgb;
    vec3 rgb11 = texture(tex, uv + vec2(+0.5, +0.5) * texelSz).rgb;

    //Get luma from the 5 samples
    float lumaCC = dot(rgbCC, luma);
    float luma00 = dot(rgb00, luma);
    float luma10 = dot(rgb10, luma);
    float luma01 = dot(rgb01, luma);
    float luma11 = dot(rgb11, luma);

    // Compute gradient from luma values
    vec2 dir = vec2((luma01 + luma11) - (luma00 + luma10), (luma00 + luma01) - (luma10 + luma11));

    // Diminish dir length based on total luma
    float dirReduce = max((luma00 + luma10 + luma01 + luma11) * reduce_mul, reduce_min);

    // Divide dir by the distance to nearest edge plus dirReduce
    float rcpDir = 1.0 / (min(abs(dir.x), abs(dir.y)) + dirReduce);

    // Multiply by reciprocal and limit to pixel span
    dir = clamp(dir * rcpDir, -span_max, span_max) * texelSz.xy;

    // Average middle texels along dir line
    vec4 A = 0.5 * (
    texture(tex, uv - dir * (1.0 / 6.0))
    + texture(tex, uv + dir * (1.0 / 6.0))
    );

    // Average with outer texels along dir line
    vec4 B = A * 0.5 + 0.25 * (
    texture(tex, uv - dir * (0.5))
    + texture(tex, uv + dir * (0.5))
    );


    // Get lowest and highest luma values
    float lumaMin = min(lumaCC, min(min(luma00, luma10), min(luma01, luma11)));
    float lumaMax = max(lumaCC, max(max(luma00, luma10), max(luma01, luma11)));

    // Get average luma
    float lumaB = dot(B.rgb, luma);

    //If the average is outside the luma range, using the middle average
    return ((lumaB < lumaMin) || (lumaB > lumaMax)) ? A : B;
}

