			B += (uv.x - x) / pow(pow(uv.x - x, 2.) + pow(uv.y - y, 2.), 1.5);
		}
	}
	for (float x = 0.05; x <= 0.1; x += 0.001)
	{
		for (float y = 0.; y <= 0.2; y += 0.005)
		{
			B += (x - uv.x) / pow(pow(uv.x - x, 2.) + pow(uv.y - y, 2.), 1.5);
		}
	}

	for (float angle = 0.; angle < Pi / 2.; angle = angle + 0.075)
	{
		for (float r = 0.05; r <= 0.1; r += 0.001) 
		{
			B += (uv.y * sin(angle) + uv.x * cos(angle) + r) / pow(uv.x * uv.x + uv.y * uv.y + 2. * uv.x * r * cos(angle) + 2. * uv.y * r * sin(angle) + r * r, 1.5);
		}
	}
	for (float angle = Pi; angle > Pi / 2.; angle = angle - 0.075)
	{
		for (float r = 0.05; r <= 0.1; r += 0.001)
		{
			B += (uv.y * sin(angle) + uv.x * cos(angle) + r) / pow(uv.x * uv.x + uv.y * uv.y + 2. * uv.x * r * cos(angle) + 2. * uv.y * r * sin(angle) + r * r, 1.5);
		}
	}

    B=pow(pow(B,2.),0.5);
    for(float i=0.;i<=10.;i++)
    {
        if(B > 10000.*i && B < 10000.*i+1000.){
            col=vec3(0.);            
            break;
        }
    }
    for(float i=5.;i<=20.;i++)
    {
        if(B > 20000.*i && B < 20000.*i+3000.){
            col=vec3(0.);            
            break;
        }
    }
     for(float i=2.;i<=10.;i++)
    {
        if(B > 200000.*i && B < 200000.*i+20000.){
            col=vec3(0.);            
            break;
        }
    }

    if((step(uv.x,0.1)*step(0.05,uv.x)*step(uv.y,0.2)*step(0.,uv.y))==1.)
    {
        col=vec3(0.);
    }
     if((step(uv.x,-0.05)*step(-0.1,uv.x)*step(uv.y,0.2)*step(0.,uv.y))==1.)
    {
        col=vec3(0.);
    }
     if(step(0.05*0.05,uv.x*uv.x+uv.y*uv.y)*step(uv.x*uv.x+uv.y*uv.y,0.01)*step(uv.y,0.)==1.)
    {
        col=vec3(0.);
    }

