    float t = iTime/10.;
    vec4 v = eulerto4(0. + M.x,0.,0.);
    vec4 r = eulerto4(pi/2.+ M.x,0.,0.);
    vec4 u = eulerto4(0.+ M.x,pi/2.,0.);
    v = rot(v,0.,M.y,M.y,0.+t,0.+M.x);
    r = rot(r,0.,M.y,M.y,0.+t,0.+M.x);
    u = rot(u,0.,M.y,M.y,0.+t,0.+M.x);
    vec4 ray = normalize(v + r*uv.x + u * uv.y);
    vec4 ray_origin = vec4(0,0,0,0);

    vec4 next_ray = ray+0.;
    vec4 next_ray_origin = ray_origin+0.;

    vec4 norms[8];

    vec4 model_origin = v*11.;


    vec3 col = ray.xyz;

    // Time varying pixel color
    float lowest_d = 1000000000000.;

    vec3 totalcol=vec3(0);
    for (float reflection_depth = 0.; reflection_depth < 8.; reflection_depth++){
    col = mainloop(model_origin,ray,ray_origin,lowest_d,col,next_ray, next_ray_origin);

    totalcol+=col/3./(reflection_depth+1.4);

    ray = next_ray+0.;
