vec4 eulerto4( float a, float c ,float b)
{
    float ca = cos(a);
    float sa = sin(a);
    float cb = cos(b);
    float sb = sin(b);
    float cc = cos(c);
    float sc = sin(c);

    return normalize(vec4(ca*cc,sa*cc,cb*sc,sb*sc));
}
vec4 rot(vec4 p,float a, float b,float c,float d,float e)
{

 vec4 pt;
 float co = cos(a);
 float si = sin(a);

 pt = vec4(co*p.x - si*p.y,si*p.x + co*p.y,p.z,p.w);
 p = pt+0.;
 co = cos(b);
 si = sin(b);
 pt = vec4(co*p.x - si*p.z,p.y,si*p.x + co*p.z,p.w);
 p = pt+0.;

 co = cos(c);
 si = sin(c);
 pt = vec4(co*p.x - si*p.w,p.y,p.z,si*p.x + co*p.w);
 p = pt+0.;

 co = cos(d);
 si = sin(d);
 pt = vec4(p.x,co*p.y - si*p.w,p.z,si*p.y + co*p.w);
 p = pt+0.;

 co = cos(e);
 si = sin(e);
 pt = vec4(p.x,p.y,co*p.z - si*p.w,si*p.z + co*p.w);
 p = pt+0.;

 return p;

}
vec4 ref(vec4 ray, vec4 norm){
return ray - 2.*dot(ray,norm)*norm;
}
vec3 cube_search(vec4 cube,vec4 ray,vec4 ray_origin, vec4 norms[8],inout float lowest_d, vec3 col, vec4 total_center,inout vec4 reflected_ray,  inout vec4 reflected_ray_origin)
{
