}
float cube_dist(vec4 ray_inverse, vec4 cube_lower_corner,float size){
    vec4 cube_upper_corner = cube_lower_corner + 1.;
    vec4 dist_low = cube_lower_corner*ray_inverse;
    //vec3 sine_transform = sine(dist_low);

    vec4 dist_high = dist_low+ray_inverse*size;
    //vec3 minimums = min2(dist_low,dist_high);
    //vec3 maximums = max2(dist_low,dist_high);

    //float maximalMinimum = max3(minimums);
    //float minimalMaximum = min3(maximums);
    float minimalMaximum = minmax(dist_low,dist_high);
    float maximalMinimum = maxmin(dist_low,dist_high);

    if (minimalMaximum>maximalMinimum){
    if (maximalMinimum<=0.)
    {return minimalMaximum;}
