   return 10000000000000.;
}
vec3 col_return_cube(vec4 cube,float size,vec4 ray,vec4 ray_origin,inout float lowest_d, vec3 col,inout vec4 reflected_ray,  inout vec4 reflected_ray_origin)
{
    vec4 ray_inverse = 1./ray;
    cube-=ray_origin;
    float d = cube_dist(ray_inverse,cube,size);

    if(d<lowest_d && d>0.)
    {
        lowest_d = d;
        vec4 cube_lower_corner = cube;
        vec4 dist_low = cube_lower_corner*ray_inverse;
        vec4 dist_high = dist_low+ray_inverse*size;

        vec4 n  = vec4(dist_low.x == d, dist_low.y == d, dist_low.z == d, dist_low.w == d);
        n -= vec4(dist_high.x == d, dist_high.y == d, dist_high.z == d, dist_high.w == d);
        col = (n.xyz + n.w + .5);

        reflected_ray = ref(ray,n);
        reflected_ray_origin = ray*d +ray_origin + reflected_ray*.001;

    }
    return col;
}
vec3 col_return_cube(vec4 cube,vec4 ray,vec4 ray_origin,inout float lowest_d, vec3 col,inout vec4 reflected_ray,  inout vec4 reflected_ray_origin)
{
    return col_return_cube(cube,1.0,ray,ray_origin,lowest_d,col, reflected_ray, reflected_ray_origin);
}


vec3 sphere_search(vec4 sphere,vec4 ray,vec4 ray_origin, inout float lowest_d, vec3 col, float r, inout vec4 reflected_ray,  inout vec4 reflected_ray_origin)
{
    vec4 p0 = sphere - ray_origin;
    float gradient = dot(ray, -p0)*dot(ray, -p0) - (dot(-p0,-p0) - r*r);

    float d = -dot(ray, -p0) - sqrt(gradient);

    vec4 p = ray*d;

    vec4 intersect_to_sphere_center = p - p0;

    if(d<lowest_d&& d>0.00010){
        col =  intersect_to_sphere_center.xyz + intersect_to_sphere_center.w + .5;//intersect_to_sphere_center.xyz + intersect_to_sphere_center.w + .5;
        reflected_ray = ref(ray,normalize(intersect_to_sphere_center));
    reflected_ray_origin = p +ray_origin;

    lowest_d = min(d,lowest_d);}

    //col = vec3(gradient,mod(gradient,100.),0);
    return col;
}

vec3 sphere_inside_search(vec4 sphere,vec4 ray,vec4 ray_origin, inout float lowest_d, vec3 col, float r, inout vec4 reflected_ray,  inout vec4 reflected_ray_origin)
{
    vec4 p0 = sphere - ray_origin;
    float gradient = dot(ray, -p0)*dot(ray, -p0) - (dot(-p0,-p0) - r*r);

    float d = -dot(ray, -p0) + sqrt(gradient);

    vec4 p = ray*d;

    vec4 intersect_to_sphere_center = -p + p0;

    if(d<lowest_d&& d>0.00010){
        col =  intersect_to_sphere_center.xyz + intersect_to_sphere_center.w + .5;//intersect_to_sphere_center.xyz + intersect_to_sphere_center.w + .5;
        reflected_ray = ref(ray,normalize(intersect_to_sphere_center));
    reflected_ray_origin = p +ray_origin;

    lowest_d = min(d,lowest_d);}

    //col = vec3(gradient,mod(gradient,100.),0);
    return col;
}

/*
    Original shader by Shane here: https://www.shadertoy.com/view/4dt3zn
	I just added some audio-awareness (cube's size following the fft frenquencies)
*/

/*
	Raymarched Reflections
	----------------------

	A very basic demonstration of raymarching a distance field with reflections 
	and reasonably passable shadows. Definitely not cutting edge, but hopefully, 
	interesting to anyone who isn't quite familiar with the process.

	Reflections are pretty easy: Raymarch to the hit point, then obtain the color 
	at that point. Continue on from the hit point in the direction of the reflected 
	ray until you reach a new hit point. Obtain the color at the new point, then
	add a portion of it to your original color. Repeat the process.

	Unfortunately, the extra work can slow things down, especially when you apply
	shadows, which is probably why you don't see too many shadowed,	reflected 
	examples. However, for relatively simple distance fields, it's pretty doable.

	It was tempting to do this up, but I figured a simpler example would be more
	helpful. Take away the rambling comments, and there isn't a great deal of code.
	I'll post a more sophisticated one later.

    // Reasonably simple examples featuring reflection:

    To the road of ribbon - XT95
    https://www.shadertoy.com/view/MsfGzr

    704.2 - PauloFalcao
    https://www.shadertoy.com/view/Xdj3Dt

    // Reflections and refraction. Really cool.
    Glass Polyhedron - Nrx
    https://www.shadertoy.com/view/4slSzj

*/

#define FAR 30.

// Distance function. This one is pretty simple. I chose rounded
// spherical boxes, because they're cheap and they display the 
// reflections reasonably well.
float map(vec3 p)
{

    // Positioning the rounded cubes a little off center, in order
    // to break up the space a little.
    //
    // "floor(p)" represents a unique number (ID) for each cube 
    // (based on its unique position). Take that number and produce 
    // a randomized 3D offset, then add it to it's regular position. 
    // Simple.
    float n = sin(dot(floor(p), vec3(7, 157, 113)));
    vec3 rnd = fract(vec3(2097152, 262144, 32768)*n)*.16-.08;

    // Repeat factor. If irregularity isn't your thing, you can get 
    // rid of "rnd" to line things up again.
    p = fract(p + rnd) - .5;

    float eq = texture(iChannel0, vec2(fract(n), 0.25)).r;
    float boxsize = 0.1 + eq * 0.6;

    // Rounded spherical boxes. The following is made up, but kind of
    // makes sense. Box, minus a bit of sphericalness, gives you a 
    // rounded box.
    p = abs(p); 
    return max(p.x, max(p.y, p.z)) - boxsize + dot(p, p)*.5;

    //return length(p) - 0.225; // Just spheres.
}

// Standard raymarching routine.
float trace(vec3 ro, vec3 rd){

    float t = 0., d;

    for (int i = 0; i < 96; i++){

        d = map(ro + rd*t);

        if(abs(d)<.002 || t>FAR) break; // Normally just "d<.0025"        

        t += d*.75;  // Using more accuracy, in the first pass.
    }

    return t;
}

// Second pass, which is the first, and only, reflected bounce. 
// Virtually the same as above, but with fewer iterations and less 
// accuracy.
//
// The reason for a second, virtually identical equation is that 
// raymarching is usually a pretty expensive exercise, so since the 
// reflected ray doesn't require as much detail, you can relax things 
// a bit - in the hope of speeding things up a little.
float traceRef(vec3 ro, vec3 rd){

    float t = 0., d;

    for (int i = 0; i < 48; i++){

        d = map(ro + rd*t);

        if(abs(d)<.0025 || t>FAR) break;

        t += d;
    }

    return t;
}


// Cheap shadows are hard. In fact, I'd almost say, shadowing repeat objects - in a setting like this - with limited 
// iterations is impossible... However, I'd be very grateful if someone could prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, float k){

    // More would be nicer. More is always nicer, but not really affordable... Not on my slow test machine, anyway.
    const int maxIterationsShad = 24; 

    vec3 rd = (lp-ro); // Unnormalized direction ray.

    float shade = 1.;
    float dist = .005;    
    float end = max(length(rd), 0.001);
    float stepDist = end/float(maxIterationsShad);

    rd /= end;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i=0; i<maxIterationsShad; i++){

        float h = map(ro + rd*dist);
        //shade = min(shade, k*h/dist);
        shade = min(shade, smoothstep(0.0, 1.0, k*h/dist)); // Subtle difference. Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += min(h, .2), dist += clamp(h, .01, .2), 
        // clamp(h, .02, stepDist*2.), etc.
        dist += clamp(h, .02, .2);

        // Early exits from accumulative distance function calls tend to be a good thing.
        if (h<0.0 || dist > end) break; 
        //if (h<0.001 || dist > end) break; // If you're prepared to put up with more artifacts.
    }

    // I've added 0.5 to the final shade value, which lightens the shadow a bit. It's a preference thing. 
    // Really dark shadows look too brutal to me.
    return min(max(shade, 0.) + 0.25, 1.0); 
}

/*
// Standard normal function. It's not as fast as the tetrahedral calculation, but more symmetrical. Due to 
// the intricacies of this particular scene, it's kind of needed to reduce jagged effects.
vec3 getNormal(in vec3 p) {
	const vec2 e = vec2(0.002, 0);
	return normalize(vec3(map(p + e.xyy) - map(p - e.xyy), map(p + e.yxy) - map(p - e.yxy),	map(p + e.yyx) - map(p - e.yyx)));
}
*/

// Tetrahedral normal, to save a couple of "map" calls. Courtesy of IQ.
vec3 getNormal( in vec3 p ){

    // Note the slightly increased sampling distance, to alleviate
    // artifacts due to hit point inaccuracies.
    vec2 e = vec2(0.0035, -0.0035); 
    return normalize(
        e.xyy * map(p + e.xyy) + 
        e.yyx * map(p + e.yyx) + 
        e.yxy * map(p + e.yxy) + 
        e.xxx * map(p + e.xxx));
}

// Alternating the cube colors in a 3D checkered arrangement.
// You could just return a single color, if you wanted, but I
// thought I'd mix things up a bit.
//
// Color scheme mildly influenced by: Sound Experiment 3 - aiekick
// https://www.shadertoy.com/view/Ml2XWt
vec3 getObjectColor(vec3 p){
    vec3 col;

    // Calcula el módulo de la coordenada X, Y y Z para determinar qué color asignar al cubo.
    float modX = mod(p.x, 4.0);
    float modY = mod(p.y, 4.0);
    float modZ = mod(p.z, 4.0);

    // Asigna colores en función del módulo de las coordenadas.
    if (modX < 1.0)
        col = vec3(1.0, 0.0, 0.0); // Rojo
    else if (modX < 2.0)
        col = vec3(0.0, 1.0, 0.0); // Verde
    else if (modX < 3.0)
        col = vec3(0.0, 0.0, 1.0); // Azul
    else
        col = vec3(1.0); // Blanco

    return col;
}

// Using the hit point, unit direction ray, etc, to color the 
// scene. Diffuse, specular, falloff, etc. It's all pretty 
// standard stuff.
vec3 doColor(in vec3 sp, in vec3 rd, in vec3 sn, in vec3 lp){

    vec3 ld = lp-sp; // Light direction vector.
    float lDist = max(length(ld), 0.001); // Light to surface distance.
    ld /= lDist; // Normalizing the light vector.

    // Attenuating the light, based on distance.
    float atten = 1. / (1.0 + lDist*0.2 + lDist*lDist*0.1);

    // Standard diffuse term.
    float diff = max(dot(sn, ld), 0.);
    // Standard specualr term.
    float spec = pow(max( dot( reflect(-ld, sn), -rd ), 0.0 ), 8.0);

    // Coloring the object. You could set it to a single color, to
    // make things simpler, if you wanted.
    vec3 objCol = getObjectColor(sp);

    // Combining the above terms to produce the final scene color.
    vec3 sceneCol = (objCol*(diff + 0.15) + vec3(1., .6, .2)*spec*2.) * atten;


    // Return the color. Performed once every pass... of which there are
    // only two, in this particular instance.
    return sceneCol;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // Screen coordinates.
	vec2 uv = (fragCoord.xy - iResolution.xy*.5) / iResolution.y;

    // Unit direction ray.
    vec3 rd = normalize(vec3(uv, 1.0));


    // Some cheap camera movement, for a bit of a look around. I use this far
    // too often. I'm even beginning to bore myself, at this point. :)
    float cs = cos(iTime * 0.25), si = sin(iTime * 0.25);
    rd.xy = mat2(cs, si, -si, cs)*rd.xy;
    rd.xz = mat2(cs, si, -si, cs)*rd.xz;

    // Ray origin. Doubling as the surface position, in this particular example.
    // I hope that doesn't confuse anyone.
    vec3 ro = vec3(0., 0., iTime*1.5);

    // Light position. Set in the vicinity the ray origin.
    vec3 lp = ro + vec3(0., 1., -.5);


    // FIRST PASS.

    float t = trace(ro, rd);

    // Fog based off of distance from the camera.
    float fog = smoothstep(0., .95, t/FAR);

    // Advancing the ray origin, "ro," to the new hit point.
    ro += rd*t;

    // Retrieving the normal at the hit point.
    vec3 sn = getNormal(ro);

    // Retrieving the color at the hit point, which is now "ro." I agree, reusing 
    // the ray origin to describe the surface hit point is kind of confusing. The reason 
    // we do it is because the reflective ray will begin from the hit point in the 
    // direction of the reflected ray. Thus the new ray origin will be the hit point. 
    // See "traceRef" below.
    vec3 sceneColor = doColor(ro, rd, sn, lp);

    // Checking to see if the surface is in shadow. Ideally, you'd also check to
    // see if the reflected surface is in shadow. However, shadows are expensive, so
    // it's only performed on the first pass. If you pause and check the reflections,
    // you'll see that they're not shadowed. OMG! - Better call the shadow police. :)
    float sh = softShadow(ro, lp, 16.);


    // SECOND PASS - REFLECTED RAY

    // Standard reflected ray, which is just a reflection of the unit
    // direction ray off of the intersected surface. You use the normal
    // at the surface point to do that. Hopefully, it's common sense.
    rd = reflect(rd, sn);


    // The reflected pass begins where the first ray ended, which is the suface
    // hit point, or in a few cases, beyond the far plane. By the way, for the sake
    // of simplicity, we'll perform a reflective pass for non hit points too. Kind
    // of wasteful, but not really noticeable. The direction of the new ray will
    // obviously be in the direction of the reflected ray. See just above.
    //
    // To anyone who's new to this, don't forgot to nudge the ray off of the 
    // initial surface point. Otherwise, you'll intersect with the surface
    // you've just hit. After years of doing this, I still forget on occasion.
    t = traceRef(ro +  rd*.01, rd);

    // Advancing the ray origin, "ro," to the new reflected hit point.
    ro += rd*t;

    // Retrieving the normal at the reflected hit point.
    sn = getNormal(ro);

    // Coloring the reflected hit point, then adding a portion of it to the final scene color.
    // How much you add is up to you, but I'm going with 35 percent.
    sceneColor += doColor(ro, rd, sn, lp)*.35;


    // APPLYING SHADOWS
    //
    // Multiply the shadow from the first pass by the final scene color. Ideally, you'd check to
    // see if the reflected point was in shadow, and incorporate that too, but we're cheating to
    // save cycles and skipping it. It's not really noticeable anyway. By the way, ambient
    // occlusion would make it a little nicer, but we're saving cycles and keeping things simple.
    sceneColor *= sh;

    // Technically, it should be applied on the reflection pass too, but it's not that
    // noticeable, in this case.
    sceneColor = mix(sceneColor, vec3(0), fog); 




    // Clamping the scene color, performing some rough gamma correction (the "sqrt" bit), then 
    // presenting it to the screen.
	fragColor = vec4(sqrt(clamp(sceneColor, 0.0, 1.0)), 1.0);
}

#define eps 0.005
#define far 40.
#define time iTime*.25
#define PI 3.1415926

#define PSD pow(abs(textureLod(iChannel0, vec2(.5), 0.).r), 2.)

// Variants
//#define rings
//#define polar
//#define warp

#define audio

vec2 rotate(vec2 p, float a)
{
    float t = atan(p.y, p.x)+a;
    float l = length(p);
    return vec2(l*cos(t), l*sin(t));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

vec3 tri(in vec3 x){return abs(x-floor(x)-.5);} // Triangle function.

float distort(vec3 p)
{
    return dot(tri(p+time) + sin(tri(p+time)), vec3(.666));
}

float trap;

float map(vec3 p)
{
    p.z += .2;
    p += distort(p*distort(p))*.1;
    #ifdef audio
    trap = dot(sin(p), 1.-abs(p-PSD))*1.2;
    #endif
    float d = -sdTorus(p, vec2(1., .7)) + distort(p)*.05;

    #ifdef rings
    p.y -= .2;
    for(int i = 0; i < 3; i++)
    {
        p.y += float(i)*.1;

        d = min(d, sdTorus(p, vec2(.75, .01))-distort(p*float(i))*.01);
    }
    #endif

    return d;
}

vec3 calcNormal(vec3 p)
{
    vec2 e = vec2(eps, 0);
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
        ));
}

float trace(vec3 r, vec3 d, float start)
{
    float m, t=start;
    for (int i = 0; i < 100; i++)
    {
        m = map(r + d * t);
        t += m;
        if (m < eps || t > far) break;
    }
    return t;
}

void mainImage( out vec4 f, in vec2 g )
{
    vec2 R=iResolution.xy, u = (g+g-R)/R.y;

    #ifdef audio
    u *= sin(PSD*5.);
    #else
    u *= 0.5; // Reduce la velocidad del movimiento cuando no hay sonido
    #endif

    #ifdef polar
    u = rotate(u, 2.*atan(u.y, u.x) + time);
    #endif

    #ifdef warp
    u = abs(u)/dot(u, u) - vec2(step(1., time));
    #endif

    vec3 r = vec3(0, 0, 1), d = normalize(vec3(u, -1)), p, n, col;
    col = vec3(0.);
    float t = trace(r, d, 0.);
    p = r + d * t;

    n = calcNormal(p);

    if (t < far)
    {
        vec3 objcol = vec3(trap/abs(1.-trap), trap*trap, 1.-trap);
        vec3 lp = vec3(1, 3, 3);
        vec3 ld = lp - p;
        float len = length(ld);
        float atten = max(0., 1./(len*len));
        ld /= len;
        float amb = .25;
        float diff = max(0., dot(ld, n));
        float spec = pow(max(0., dot(reflect(-ld, n), r)), 8.);
        float ref = trace(r, reflect(d, n), eps*5.);
        col = objcol * (((diff*.8+amb*.8)+.1*spec)+atten*.1)*ref;
    }

    f = vec4(col, 1);
}

precision mediump float;

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts

vec4 permute(vec4 x) {
    return mod(((x * 34.0) + 1.0) * x, 289.0);
}

vec4 taylorInvSqrt(vec4 r) {
    return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v){ 
    const vec2 C = vec2(1.0 / 6.0, 1.0 / 3.0);
    const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);

    // First corner
    vec3 i  = floor(v + dot(v, C.yyy));
    vec3 x0 =   v - i + dot(i, C.xxx);

    // Other corners
    vec3 g = step(x0.yzx, x0.xyz);
    vec3 l = 1.0 - g;
    vec3 i1 = min( g.xyz, l.zxy);
    vec3 i2 = max( g.xyz, l.zxy);

    //  x0 = x0 - 0. + 0.0 * C 
    vec3 x1 = x0 - i1 + 1.0 * C.xxx;
    vec3 x2 = x0 - i2 + 2.0 * C.xxx;
    vec3 x3 = x0 - 1. + 3.0 * C.xxx;

    // Permutations
    i = mod(i, 289.0); 
    vec4 p = permute(permute(permute( 
        i.z + vec4(0.0, i1.z, i2.z, 1.0)) +
        i.y + vec4(0.0, i1.y, i2.y, 1.0)) +
        i.x + vec4(0.0, i1.x, i2.x, 1.0)
    );

    // Gradients
    // ( N*N points uniformly over a square, mapped onto an octahedron.)
    float n_ = 1.0/7.0; // N=7
    vec3  ns = n_ * D.wyz - D.xzx;

    vec4 j = p - 49.0 * floor(p * ns.z *ns.z); // mod(p,N*N)

    vec4 x_ = floor(j * ns.z);
    vec4 y_ = floor(j - 7.0 * x_ ); // mod(j,N)

    vec4 x = x_ *ns.x + ns.yyyy;
    vec4 y = y_ *ns.x + ns.yyyy;
    vec4 h = 1.0 - abs(x) - abs(y);

    vec4 b0 = vec4( x.xy, y.xy );
    vec4 b1 = vec4( x.zw, y.zw );

    vec4 s0 = floor(b0)*2.0 + 1.0;
    vec4 s1 = floor(b1)*2.0 + 1.0;
    vec4 sh = -step(h, vec4(0.0));

    vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
    vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

    vec3 p0 = vec3(a0.xy,h.x);
    vec3 p1 = vec3(a0.zw,h.y);
    vec3 p2 = vec3(a1.xy,h.z);
    vec3 p3 = vec3(a1.zw,h.w);

    //Normalise gradients
    vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
    p0 *= norm.x;
    p1 *= norm.y;
    p2 *= norm.z;
    p3 *= norm.w;

    // Mix final noise value
    vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
    m = m * m;
    return 42.0 * dot(m*m, vec4(dot(p0,x0), dot(p1,x1), dot(p2,x2), dot(p3,x3)));
}

float animationMultiplier(float t){
    return smoothstep(0.0, 1.0, max(0.0, 1.0 - abs((t - 0.5) * 2.0)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y;

    float pixelSize = iResolution.y / 24.0;

    vec2 pixel = mod(fragCoord, pixelSize) - pixelSize / 2.;

    fragColor = vec4(0.0);

    float noise = snoise(vec3(floor(fragCoord/pixelSize), iTime));
    float noise2 = snoise(vec3(floor(fragCoord/pixelSize) - pixelSize * 24.0, iTime));

    if(length(pixel) < pixelSize / 2.0) {
        vec4 color = texture(iChannel0, uv);

        float gray = (color.r + color.g + color.b) / 3.0 * 2.0;

        fragColor = mix(
            vec4(vec3(gray), 1.0),
            color * floor((noise * 0.4 + 0.75) * 8.0) / 8.0,
            iMouse.x/iResolution.x +
            noise2 * animationMultiplier(iMouse.x/iResolution.x)
        );
    }
}

void mainImage( out vec4 col, in vec2 pos )
{
    col = texture(iChannel0, pos.xy/iResolution.xy);
    if (texelFetch(iChannel0, ivec2(iResolution.x-1.,0.), 0).x > 0.) {
        col.x = 0.;
        if (col.z == 1.) {col = vec4(2.);}
    }// else if (col.y == 0.) {col.x /= float(iFrame);}

    if (col.y == 1.) {col = vec4(0.5);}
}

//////////////////////////////////////////////////////////////////////
uint seed = 0u;
void hash(){
    seed ^= 2747636419u;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
}
void initRandomGenerator(vec2 fragCoord){
    seed = uint(fragCoord.y*iResolution.x + fragCoord.x)+uint(iFrame)*uint(iResolution.x)*uint(iResolution.y);
}

float random(){
    hash();
    return float(seed)/4294967295.0;
}
/////////////////////////////////////////////////////////////////////

void mainImage( out vec4 col, in vec2 pos )
{
    ivec2 xy = ivec2(pos);

    if ( iFrame == 0 || iMouse.w > 0. || texelFetch(iChannel0, ivec2(0,iResolution.y-1.), 0).z==1. ) {
        col = vec4(0,0,0,0);
        initRandomGenerator(pos);
        if (random()>0.6) { col = vec4(0,1,0,0); }
        if (pos.x < 7. && pos.y > iResolution.y-7.) { col = vec4(0,0,0,0); }
        if (pos.y < 7. && pos.x > iResolution.x-7.) { col = vec4(0,0,0,0); }
        if (pos.x < 1. && pos.y > iResolution.y-1.) { col = vec4(1,0,0,0); }
    } else {
        col = texelFetch(iChannel0, xy, 0);
        if (col.y == 1.) {return;}
        vec4 W = vec4(0), A = vec4(0), S = vec4(0), D = vec4(0);
        W = texelFetch(iChannel0, xy+ivec2(0,-1), 0);
        A = texelFetch(iChannel0, xy+ivec2(-1,0), 0);
        S = texelFetch(iChannel0, xy+ivec2(0,1), 0);
        D = texelFetch(iChannel0, xy+ivec2(1,0), 0);
        if ( texelFetch(iChannel0, ivec2(iResolution.x-1.,0.), 0).x == 0. ) {
            float m = max(max(W.x, A.x), max(S.x, D.x));
            if ( col.x==0. && m > 0. ) {
                col.x = m+1.;
            }
        } else {
            if (pos.y < 1. && pos.x > iResolution.x-1.) { col.z = 1.; }
            if ( (W.z==1.&&W.x==col.x+1.) || (A.z==1.&&A.x==col.x+1.) ||
                 (S.z==1.&&S.x==col.x+1.) || (D.z==1.&&D.x==col.x+1.) ) { col.z = 1.; }
        }
    }

}

vec4 f_o_convolved_static_3x3(
    vec2 o_trn, 
    float[3*3] a_n_factor_weight_krnl, 
    int[3*3] a_n_exponent_weight_krnl
){

    vec2 o_scl_krnl = vec2(3.);
    vec2 o_scl_krnl_half = floor(o_scl_krnl/2.);


    vec4 o_col_sum = vec4(0.);

    for(float n_x = 0.; n_x < o_scl_krnl.x; n_x+=1.){
        for(float n_y = 0.; n_y < o_scl_krnl.y; n_y+=1.){
            vec2 o_trn_krnl = vec2(n_x, n_y);
            vec2 o_trn_krnl2 = o_trn_krnl-o_scl_krnl_half;
            float n_idx_a_n_krnl = n_y*o_scl_krnl.x + n_x;
            float n_factor = a_n_factor_weight_krnl[int(n_idx_a_n_krnl)];
            int n_exponent = a_n_exponent_weight_krnl[int(n_idx_a_n_krnl)];

            vec4 o_col_tmp =
                texture(
                    iChannel0,
                    (o_trn+o_trn_krnl2.xy)/iResolution.xy
                )*n_factor;
            o_col_tmp = pow((o_col_tmp), vec4((n_exponent)));
            o_col_sum+= o_col_tmp;
        }
    }
    vec4 o_col_res = o_col_sum;
    return (o_col_res);


}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trn = fragCoord.xy;
    float n_pixelation = 1.;
    o_trn=floor(o_trn*(1./n_pixelation))*n_pixelation;
    vec2 o_trn_nor = fragCoord.xy/iResolution.xy;
    vec4 o_mouse_nor = iMouse/vec4(iResolution.xy, 1.,1.);

    vec4 o_col_img = texture(iChannel0,(o_trn)/iResolution.xy);
    float n = 1./(3.*3.);

        vec4 o_col_sobel_x = f_o_convolved_static_3x3(
            o_trn,
            float[](
            //sobel edge detection x
            -1., 0., 1.,
            -2., 0., 2.,
            -1., 0., 1.
            ),
            int[](
            1, 1, 1,
            1, 1, 1,
            1, 1, 1
            )
        );
        vec4 o_col_sobel_y = f_o_convolved_static_3x3(
            o_trn,
            float[](
            //sobel edge detection y
            1., 2., 1.,
            0., 0., 0.,
            -1., -2., -1.
            ),
            int[](
            1, 1, 1,
            1, 1, 1,
            1, 1, 1
            )
        );
        vec4 o_col_sobel_x_pow = pow(o_col_sobel_x,vec4(2.));
        vec4 o_col_sobel_y_pow = pow(o_col_sobel_y,vec4(2.));
    vec4[] a_o_col = vec4[](
        o_col_img,
        f_o_convolved_static_3x3(
            o_trn,
            float[](
            //slight blur
            1./16., 1./8., 1./16.,
            1./8., 1./4., 1./8., 
            1./16., 1./8., 1./16. 
            ),
            int[](
            1, 1, 1,
            1, 1, 1,
            1, 1, 1
            )
        ), 
        f_o_convolved_static_3x3(
            o_trn,
            float[](
            //sharpening
            -1., -1., -1.,
            -1., 9., -1., 
            -1., -1., -1.
            ),
            int[](
            1, 1, 1,
            1, 1, 1,
            1, 1, 1
            )
        ), 
        f_o_convolved_static_3x3(
            o_trn,
            float[](
            //slight 'de'blur / deconvolve
            -1./16., -1./8., -1./16.,
            -1./8., 1., -1./8., 
            -1./16., -1./8., -1./16. 
            ),
            int[](
            1, 1, 1,
            1, 1, 1,
            1, 1, 1
            )
        ), 
                f_o_convolved_static_3x3(
            o_trn,
            float[](
            //laplacian sharpening
            0.0, -1.0, 0.0,
           -1.0,  5.0, -1.0,
            0.0, -1.0, 0.0
            ),
            int[](
            1, 1, 1,
            1, 1, 1,
            1, 1, 1
            )
        ), 

        (vec4(o_col_sobel_x_pow)),
