const float forcingRate = 1.;
const float forcingInvSize = .5;
const int paletteSize = 50;
const int paletteOffset = 2;
const int pureStripSize = 10;
const ivec2 indicatorLoc = ivec2(10, 62);
const int indicatorSize = 8;

// model parameters
const vec3 diffusionCoeffs = vec3(.4, .1, 0.);
const float preyGrowthRate = .1;
const float preyInvCarryingCapacity = 1./1.1;
const float predationRate = .5;
const float conversionRate = .3;
const float predatorDeathRate = .1;

bool inDomain(ivec2 coord)
{
    return length(vec2(coord) - .5*iResolution.xy) < .46875*iResolution.y;
}

bool inPalette(ivec2 coord)
{
    return coord.x >= paletteOffset && coord.x <= paletteSize + paletteOffset &&
           coord.y >= paletteOffset && coord.y <= paletteSize + paletteOffset;
}

vec3 getMixture(vec2 fragCoord)
{
    return vec3(clamp((fragCoord - vec2(paletteOffset + pureStripSize)) / vec2(paletteSize - pureStripSize), 0., 1.), 0.);
}

vec3 getPopulations(ivec2 coord)
{
    return texelFetch(iChannel0, ivec2(coord),0).rgb;
}

// used to enforce Neumann boundary conditions
vec3 getPopulationsBounded(ivec2 coord, vec3 fallback)
{
    if(inDomain(coord)) return getPopulations(coord);
    return fallback;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord);
    vec3 forcingMixture = getPopulations(indicatorLoc);

    fragColor = vec4(0.);

    if(abs(fragCoord.x - .5*iResolution.x) < .5*iResolution.y)
        fragColor = vec4(1.,1.,1.,0.);    

    ivec2 off = abs(coord - indicatorLoc);
    if(off.x <= indicatorSize && off.y <= indicatorSize) {
        if(iMouse.z > 0.0 && inPalette(ivec2(iMouse.xy))) {
            forcingMixture = getMixture(iMouse.xy);
        }
        fragColor.rgb = forcingMixture;
    }

    if(inDomain(coord)) {
        fragColor = vec4(0.,0.,0.,1.);
        vec3 curPops = getPopulations(coord);

        // Oono-Puri nine-point stencil Laplacian
        vec3 laplacian = -3.*curPops
            + .25 * getPopulationsBounded(coord + ivec2(1, 1), curPops)
            + .25 * getPopulationsBounded(coord + ivec2(1, -1), curPops)
            + .25 * getPopulationsBounded(coord + ivec2(-1, 1), curPops)
            + .25 * getPopulationsBounded(coord + ivec2(-1, -1), curPops)
            + .5 * getPopulationsBounded(coord + ivec2(1, 0), curPops)
            + .5 * getPopulationsBounded(coord + ivec2(0, 1), curPops)
            + .5 * getPopulationsBounded(coord + ivec2(-1, 0), curPops)
            + .5 * getPopulationsBounded(coord + ivec2(0, -1), curPops);

        curPops +=
            // diagonal diffusion matrix
            diffusionCoeffs * laplacian +
            // Kolmogorov driver
            curPops * vec3(
                // predator
                conversionRate * curPops.g - predatorDeathRate,
                // prey
                preyGrowthRate * (1. - curPops.g * preyInvCarryingCapacity) - predationRate * curPops.r,
                // unused
                0.
            );

        if(iMouse.z > 0.0) {
            // inject populations as a gaussian
            curPops += forcingRate * forcingMixture * exp( 
                -forcingInvSize * dot(iMouse.xy - fragCoord, iMouse.xy - fragCoord));
        }

        fragColor.rgb = curPops;
    } else if(inPalette(coord)) {
        fragColor.rgb = getMixture(fragCoord);
    }
}

/*this is a renderer for a triangle it takes three cornerpoints (a ,b and c)
  and one point p, it gives back a vector with three values
  the first falue is 1 if p is inside the triangle and 0 if ouside.
  the last two values are the uv cordinates for the triangle wich you can
  use to display a texture on the triangle.
  */


vec3 t (vec2 p,vec2 a,vec2 b,vec2 c){
    float w1 = (a.x*(c.y-a.y)+(p.y-a.y)*(c.x-a.x)-p.x*(c.y-a.y))/
               ((b.y-a.y)*(c.x-a.x)-(b.x-a.x)*(c.y-a.y));

    float w2 = (p.y-a.y-w1*(b.y-a.y))/(c.y-a.y);

    float i  = 1.;
    if(min(w1,w2)<=0.)i=0.;
    if(w1+w2>=1.)i=0.;

    return vec3(i,w1,w2);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec2 a  = vec2(0.1,0.9);
    vec2 b  = vec2(0.1,0.1);
    vec2 c  = vec2(0.9,0.1);

    vec3 tc = t(uv,a,b,c);

    vec4 e  = texture(iChannel0,tc.yz,0.)*(tc.x);

    fragColor = e;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    vec2 view = uv*2.0 - vec2(1.0);
    view.x *= ratio;

    vec4 image;
    int amount = abs(int(iTime*0.4)%12-6)+3;
    vec3 points;

    for (int i = 0; i<amount; i++){
    float iF = float(i)*3.14*2./float(amount)*sin(iTime/2.);
    points = vec3(sin(iTime+iF),cos(iTime+iF),cos(iTime*9.+iF*3.)*2.+3.);
    points /= 1.8;

    int iFract = i%3;
    image[iFract] +=  pow(max(0.,1.-distance(view*points.z,points.xy)*1.5),4.);
    }




    fragColor = image;
}

