void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime * 0.3;

    // Background color
    vec3 col = vec3(0.0, 0.0, 0.1);

    // Circular waves
    float circles = 0.0;
    for (int i = 0; i < 5; i++) {
        float t = float(i) * 0.1 + time;
        float scale = 0.3 + sin(t) * 0.1;
        vec2 center = vec2(0.5) + vec2(sin(t*1.2), cos(t*0.7)) * scale;
        float dist = distance(uv, center);
        circles += smoothstep(0.2, 0.0, dist) * (sin(dist*50.0-t*10.0)+1.0) * 0.5;
    }
    col += circles * vec3(0.0, 0.5, 1.0);

    // Pulsing grid
    vec2 grid = fract(uv * 20.0);
    float gridMix = min(grid.x, grid.y);
    float gridPulse = smoothstep(0.92, 0.98, sin(time*5.0 + gridMix*3.14));
    col += gridPulse * vec3(0.0, 1.0, 0.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
