float rand(vec2 co, float seed)
{
    uvec2 p = floatBitsToUint(co);
    uint s = floatBitsToUint(seed);
    uint is = ~s;
    uvec2 ip = ~p;

    p ^= p << 17U;
    p ^= p >> 13U;
    p ^= p << 5U;
    p += (ip * s + p.y * p.x);
    p *= 1013U;

    uint n = (p.x * is + p.y)*5013U;

    return float(n) / float(0xFFFFFFFFU);
}

float noise(vec2 co, float seed) {
    vec2 lv = fract(co); lv = lv*lv*(3.0-2.0*lv);
    vec2 id = floor(co);

    return mix(
        mix(rand(id + vec2(0, 0), seed), rand(id + vec2(1, 0), seed), lv.x),
        mix(rand(id + vec2(0, 1), seed), rand(id + vec2(1, 1), seed), lv.x),
        lv.y
    );
}

float noise(vec2 co, float seed, int oct) {
    float n = 0.0;
    float div = 0.0;
    float amp = 1.0;
    float freq = 1.0;

    for (int i = min(iFrame, 0); i < oct; i++) {
        n += amp*noise(co*freq, seed);
        float np = n*6.28;
        co += 0.15*amp*vec2(sin(np), cos(np));
        div += amp;
        amp *= 0.5;
        freq *= 2.0;
    }

    return n / div;
}

vec3 randomColor(vec2 co, float seed) {
    co *= 9.0;
    float r = noise(co, seed, 6);
    float g = noise(co, seed+4.499123, 6);
    float b = noise(co, seed+19.58389, 6);
    return vec3(r, g, b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec4 m = vec4((iMouse.xy - 0.5 * iResolution.xy) / iResolution.y, iMouse.zw);
    vec2 point = vec2(0, 0);

    if (m.z > 0.001) {
        point = m.xy;
    } else {
        float t = iTime*0.25;
        point += 0.15*vec2(sin(t), cos(t)); 
    }

    int st = 1;

    vec3 right = texelFetch(iChannel0, ivec2(fragCoord.xy) + ivec2(st, 0), 0).rgb;
    vec3 left = texelFetch(iChannel0, ivec2(fragCoord.xy) + ivec2(-st, 0), 0).rgb;
    vec3 up = texelFetch(iChannel0, ivec2(fragCoord.xy) + ivec2(0, st), 0).rgb;
    vec3 down = texelFetch(iChannel0, ivec2(fragCoord.xy) + ivec2(0, -st), 0).rgb;


    vec3 old = iFrame <= 1 ? randomColor(uv, iTime) : texelFetch(iChannel0, ivec2(fragCoord.xy), 0).rgb;

    vec3 col = randomColor(uv, iTime);

    vec3 cp = cross(col*2.0-1.0, old*2.0-1.0);
    col = length(cp) > 0.00001 ? abs(normalize(cp)) : col;

    int comp = 2;
    float r = 0.15;
    float f = r*0.5;
    float h = 0.5;
    float dist = smoothstep(r-f, r+f, distance(uv, point));

    comp = clamp(int(mix(0.0, 2.05, dist)), 0, 2);
    h = mix(h, 0.25, dist);

    if (right[comp] > h && left[comp] > h && up[comp] > h && down[comp] > h) {
        vec3 ot = min(right, min(left, min(up, down))).xyz;
        col = mix(col, ot, 1.01);        
    }

    fragColor = vec4(mix(col, old, mix(0.5, 0.9, smoothstep(2.0, 2.5, min(iTime, 3.0)))), 1.0);
}

