void mainImage( out vec4 O,  vec2 U )
{
	O = texture( iChannel0, U / iResolution.xy);
}

// --- noise from procedural pseudo-Perlin (better but not so nice derivatives) ---------
                    // ( adapted from IQ )

float noise3( vec3 x ) {
    vec3 p = floor(x),f = fract(2.0*x);

    f = f*f*(3.0-8.*f);  // or smoothstep     // to make derivative continuous at borders

#define hash3(p)  fract(sin(1e3*dot(p,vec3(1,57,-13.7)))*4375.5453)        // rand

    return mix( mix(mix( hash3(p+vec3(0,0,0)), hash3(p+vec3(1,0,0)),f.x),       // triilinear interp
                    mix( hash3(p+vec3(0,1,0)), hash3(p+vec3(1,1,0)),f.x),f.y),
                mix(mix( hash3(p+vec3(0,0,1)), hash3(p+vec3(1,0,1)),f.x),       
                    mix( hash3(p+vec3(0,1,1)), hash3(p+vec3(1,1,1)),f.x),f.y), f.z);
}

#define noise(x) (noise3(x)+noise3(x+11.5)) / 2. // pseudoperlin improvement from foxes idea 



void mainImage( out vec4 O, vec2 U ) // ------------ draw isovalues
{ 
    vec2 R = iResolution.xy;
    float n = noise(vec3(U*8./R.y, .01*iTime)),
          v = sin(6.28*12.*n),
        t = 15.0*iTime;

    v = smoothstep(1.,0., .1*abs(v)/fwidth(v));

	O = mix( exp(-1./R.y )* texture( iChannel0, (U+vec2(1,sin(t)))/R), // .97
             .5+.4*sin(12.*n+vec4(0,0,0,0)),
             v );

}

float PI = 3.14159265358979323846;
float TWOPI = 6.28318530718;
float PIONTWO = 1.57079632679;

vec3 palette1(float t)
{
    vec3 a = vec3(0.4,0.4,0.5);
    vec3 b = vec3(0.2,0.2,0.5);
    vec3 c = vec3(1.0,1.0,0.5);
    vec3 d = vec3(0.05,0.15,0.25);
    return( vec3( a + b*cos( TWOPI*( c*t + d ) ) ) );
}

float norm(vec2 uv)
{
    return( uv.x*uv.x + uv.y*uv.y );
}

vec2 square(vec2 uv)
{
    return( vec2(uv.x*uv.x - uv.y*uv.y , 2.0*uv.x*uv.y) );
}

vec2 cube(vec2 uv)
{
    return vec2(uv.x*uv.x*uv.x - 3.0*uv.x*uv.y*uv.y , 3.0*uv.x*uv.x*uv.y - uv.y*uv.y*uv.y);
}

vec2 quad(vec2 uv)
{
    float x2 = uv.x*uv.x ; float y2 = uv.y*uv.y; float xy = uv.x*uv.y;
    return( vec2(x2*x2 - 6.0*x2*y2 + y2*y2 , 4.0*xy*(x2 - y2) ) );
}

vec2 pent(vec2 uv)
{
    float x2 = uv.x*uv.x; float y2 = uv.y*uv.y; float xy = uv.x*uv.y;
    float x3 = x2*uv.x; float y3 = y2*uv.y;
    return( vec2(x2*x3 - 10.0*x3*y2 + 5.0*y3*xy  , 5.0*xy*x3 - 10.0*x2*y3 + y2*y3 ) );
}

vec2 times(vec2 x, vec2 y)
{
    return( vec2(x.x*y.x - x.y*y.y , x.y*y.x + x.x*y.y) );
}

vec2 oneover(vec2 uv)
{
    return( vec2(uv.x , -uv.y)/norm(uv) );
}

vec2 itimes(vec2 uv)
{
    return( vec2(-uv.y,uv.x) );
}

vec2 exp2d(vec2 uv)
{
    return( exp(uv.x)*vec2(cos(uv.y),sin(uv.y)));
}

vec2 cos2d(vec2 uv)
{
    return ( vec2 (exp2d(itimes(uv)) + exp2d(itimes(-uv)))*0.5);
}

vec2 sin2d(vec2 uv)
{
    return ( vec2 (-itimes((exp2d(itimes(uv)) - exp2d(itimes(-uv)))*0.5)));
}


float atan2(float y, float x)
{
    //this computes arctan(y/x)
    float angle = atan(y,x);
    if(angle < 0.0){angle+= TWOPI;}
    return(angle);
}

vec2 power(vec2 uv, float n)
{
    float modulus = length(uv);
    float angle = atan2(uv.y,uv.x);
    angle *= n;

    return( exp(n*log(modulus))*vec2(cos(angle),sin(angle)));
}

vec2 log2d(vec2 uv)
{
    return(vec2(log(length(uv)),atan(uv.y,uv.x)));
}


vec2 f(vec2 uv, vec2 c)
{
    //vec2 m = vec2(1.0,0.0) + (iMouse.xy/iResolution.xy - 0.5)*6.0;
    //return( cube(uv) + times(c,uv) - vec2(0.5));
    return( cube(uv) + times(c,uv) - vec2(0.5));
}

vec2 fprime(vec2 uv, vec2 c)
{
    return( 2.0*square(uv) + c );
}

vec2 NewtonIteration(vec2 uv, vec2 c, vec2 a)
{
    return(uv - times(a, times(f(uv,c) , oneover(fprime(uv,c)) ) ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - 0.5)*10.0;
    uv.y *= iResolution.y/iResolution.x;
    float zoom = pow(10.0,-iTime/5.0); uv *= zoom;
    vec2 m = vec2(1.0,0.0) + (iMouse.xy/iResolution.xy - 0.5)*3.0;
    vec2 zoomPoint = vec2(0); uv += zoomPoint;
    vec2 dummyuv = uv; vec2 previousuv = vec2(10000.0,-2451.0); float distanceToPrevious;
    float maxIteration = 1000.0; int i = 0; float tolerance = pow(10.0,-8.0);

    int hasStopped = 0; //0 means not enough iter. 1 means converged. 2 means stuck without converging
    float dist;
    vec2 c; c = dummyuv; 
    uv = vec2(0.0);
    //c = m; 
    for (i = 0; i < int(maxIteration); i++)
    {
        dist = norm(f(uv,c));
        distanceToPrevious = norm( uv - previousuv );
        if( dist < tolerance){hasStopped = 1; break;}
        else if( distanceToPrevious < tolerance ){hasStopped = 2; break;}
        previousuv = uv;
        uv = NewtonIteration(uv, c, vec2(1.0,0.0));
    }

    vec3 finalColor;

    if (hasStopped == 1)//converged
    {
        float index = 2.0*log(float(i) + 1.0);
        finalColor = palette1(index);
    }
    else if(hasStopped == 2)//stuck
    { finalColor = vec3(0.0,0.2265,1.0);}

    else//not enough iter
    { finalColor = vec3(0.0,0.0,0.0);}

    fragColor = vec4(finalColor,1.0);
}

