#define END 50.

struct CAM {
    vec3 lookat;
    vec3 ro;

    vec3 forw;
    vec3 up;
    vec3 right;

    float field;
};

struct RAY {
	vec3 ro;       
    vec3 rd;
};

mat2 rot(float a)
{
    float c0 = cos(a);
    float s0 = sin(a);
    return mat2(
        c0,-s0,
        s0, c0
    );
}

