// --- Useful Funcs ---

float smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

float ssub( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}

float sint( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); 
}

float onion( in float d, in float h )
{
    return abs(d)-h;
}

// --- PRIMITIVES ---    

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

// --- SCENE ---

float sdScene(vec3 P)
{
    float d;
    P.xy *= rot(iTime * .1 - 3.8);
    P.yz *= rot(iTime * .21 + 5.6);
    #define PERIOD_LINES 10.
    #define PERIOD_CIRCLES 70.
    #define THICK_LINES .4

    float modp1 = length(mod(P.xy*PERIOD_LINES, 1.) - .5) - THICK_LINES;
    float modp2 = length(mod(P.yz*PERIOD_LINES, 1.) - .5) - THICK_LINES;
    float modp3 = length(mod(P.zx*PERIOD_LINES, 1.) - .5) - THICK_LINES;

    float circles = mod(sqrt(length(P))*PERIOD_CIRCLES, 1.) - .05; 

    d = min(min(modp1, modp2), modp3);
    d = max(-d, circles);
    d = max(d, sdBox(P, vec3(3.5)));

    return d*.05;
}

