void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    /*
    Linear Equation to define the simplex
    2u + 3v <= 2.5
    u <= 0.75
    v <= 0.75
    u >= 0
    v >= 0
    */


    // Output to screen
    float u = uv.x ;
    float v = uv.y ;
    float l1 = 2.*u + 3.*v ;
    float l2 = u ;
    float l3 = v ;
    float l4 = u ;
    float l5 = v ;

    bool b = 
        l1 <= 2.5 &&
        l2 <= 0.75 &&
        l3 <= 0.75 &&
        l4 >= 0. &&
        l5 >= 0.; 

    fragColor = vec4(0,0,0, 1.0);
    if (b) {
        fragColor = vec4(255, 255, 255,1.0);
    }
}

float sinc(float x)
{
    float a = 3.14159 * (100.0 * x - 1.0);
    return sin(a) / a;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv    = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 mouse = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;

    float p = length(uv);
    float r = p - 0.6;

    vec3 color = sinc(p * uv.x - mouse.x)
               + sinc(p * uv.y - mouse.y)
               - 200.0 * r / (1.0 + 10000.0 * r * r)
               * vec3(0.8, 1.0, 0.9);

    fragColor = vec4(color, 1.0);
}

#define PI 3.14159265


vec3 palette(float i)
{
    return 0.5 + 0.5 * cos(i + vec3(0.0, 2.0 * PI / 3.0, 4.0 * PI / 3.0));
}

float sector(float r, float theta, float r1, float r2, float theta1, float theta2, float aa_dist)
{
    r = max(r, 0.001);
    theta1 = mod(theta1 + PI, PI * 2.0) - PI;
    theta2 = mod(theta2 + PI, PI * 2.0) - PI;
    bool reverse = theta1 > theta2;
    if (reverse) {
        float temp = theta1;
        theta1 = theta2;
        theta2 = temp;
    }
    float r_grad = smoothstep(r1 - aa_dist, r1, r)
                 * smoothstep(-r2 - aa_dist, -r2, -r);
    float theta_grad = smoothstep(theta1 - aa_dist / r, theta1, theta)
                     * smoothstep(-theta2 - aa_dist / r, -theta2, -theta);
    if (reverse) {
        theta_grad = 1.0 - theta_grad;
    }
    return r_grad * theta_grad;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy ) / iResolution.y;
    float aa_dist = 4.0 / iResolution.y;

    float theta = atan(uv.y, uv.x);
    float r = length(uv);

    vec3 col = vec3(smoothstep(-1.0, 0.0, -r)) * 0.3 * (1.4 + sin(iTime));
    for (float i = 0.0; i < 15.0; i++) {
        float r1 = i * 0.15 + 0.1;
        float r2 = r1 + 0.05;
        float theta1 = iTime * (cos(i) + 0.1) * 3.0;
        float theta2 = theta1 + i * 0.5 + 2.5;
        float intensity = sector(r, theta, r1, r2, theta1, theta2, aa_dist);
        vec3 hue = palette(i);
        col = mix(col, hue * intensity, intensity);
    }

    r *= 15.0;
    if (r < 1.0) {
        float inner_grad = 1.0 - r * r;
        col = vec3(inner_grad);
    }

    fragColor = vec4(col,1.0);
}

// Permutation table
vec3 permute(vec3 x) {
    return mod((x * 34.0 + 1.0) * x, 289.0);
}

// Simplex 2D noise function
float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                       -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
    vec2 i = floor(v + dot(v, C.yy));
    vec2 x0 = v - i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod(i, 289.0);
    vec3 p = permute(permute(i.y + vec3(0.0, i1.y, 1.0))
        + i.x + vec3(0.0, i1.x, 1.0));
    vec3 m = max(0.5 - vec3(dot(x0, x0), dot(x12.xy, x12.xy),
                             dot(x12.zw, x12.zw)), 0.0);
    m = m * m;
    m = m * m;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * (a0 * a0 + h * h);
    vec3 g;
    g.x = a0.x * x0.x + h.x * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}

// Swirly noise function
float swirlyNoise(vec2 p) {
    return snoise(p * 3.5 + vec2(0.0, iTime * 0.5));
}

// Weighted color function
vec3 weightedColor(float intensity, vec3 baseColor, vec3 blackColor) {
    return mix(blackColor, baseColor, intensity);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy; // Normalize coordinates

    // Rotate UV coordinates by 90 degrees
    uv = vec2(uv.y, uv.x);

    // Create waves using sine function
    float wave = sin(uv.x * 20.0 + iTime * 2.0) * 0.01;

    // Add swirly noise
    float noise = swirlyNoise(uv * 4.0);

    // Calculate distance from center
    vec2 center = vec2(0.5, 0.5);
    float dist = length(uv - center);

    // Create black and white stripes based on distance from center
    float stripe = smoothstep(0.01 + wave, 0.99 + wave, dist);

    // Combine stripe with noise
    stripe += noise * 0.2;

    // Clamp stripe to range [0, 1]
    stripe = clamp(stripe, 0.0, 1.0);

    // Map stripe value to color gradient
    vec3 baseColor = vec3(0.2, 0.8, 0.2); // Example color (reddish)
    vec3 blackColor = vec3(.1,.1,.4); // Customizable black color

    // Weight the color based on intensity
    vec3 finalColor = weightedColor(stripe, baseColor, blackColor);

    // Output color
    fragColor = vec4(finalColor, 1.0);
}

#define sat(x) clamp(x,0.0,1.0)

mat2 rot(float a){return mat2(cos(a),-sin(a),sin(a),cos(a));}

void mainImage(out vec4 fragColor,vec2 fragCoord){
	vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec3 col = vec3(0.0);

	vec3 ro = vec3(0,1,3);
	vec3 rd = normalize(vec3(uv,-1.5));
    //orbit camera
    rd.yz *= rot(-0.25);
	ro.xz *= rot(iTime * 0.5);
	rd.xz *= rot(iTime * 0.5);

    //color
    float k = 0.5+0.5*sin(iTime);
	vec3 ec = normalize(mix(mix(vec3(1,0.1,0.4),vec3(0.7,0.1,1),k*2.0),vec3(0.1,1,0.8),k));

    float t = 0.0;
	vec3 ol = vec3(0.0); //outline
	for(int i=0;i<256;i++){
		vec3 p = ro + rd * t;
		float h = 999999.999;

        //sdf scene
        h = min(h,length(p)-0.5);
		h = min(h,length(p-0.5)-0.4);
		h = max(h,-(length(p-vec3(0,0.8,0))-0.9));
		h = min(h,length(p-0.5)-0.2);
		h = min(h,length(p+0.5)-0.2);

        ol += (ec*0.5+0.5)*0.01;
		if(t > 100.0) break;
		if(h < 0.0002){ol = vec3(1); break;}
		t += h;
	}

    //result
	col += ec*sat(pow(ol*4.5,vec3(4.0)));

    fragColor = vec4(col,1.0);
}

// Solution to Martijn's problem
// Based on this video:
// https://youtu.be/2R7h76GoIJM

float Hash21(vec2 p) {
	p = fract(p*vec2(234.34, 435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 UV = fragCoord.xy/iResolution.xy;

    vec3 col = vec3(0);

    uv += iTime*.05;
    uv *= 5.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = Hash21(id); // random number between 0 and 1

    float width = .1;

    if(n<.5) gv.x *= -1.;
    float d = abs(abs(gv.x+gv.y)-.5);
    vec2 cUv = gv-sign(gv.x+gv.y+.001)*.5;
    d = length(cUv);
    float mask = smoothstep(.01, -.01, abs(d-.5)-width);
	float angle = atan(cUv.x, cUv.y); // -pi to pi
    float checker = mod(id.x+id.y, 2.)*2.-1.;
    float flow = sin(iTime+checker*angle*10.);

    float x = (checker*angle/1.57-iTime*.3);
    float y = (d-(.5-width))/(2.*width);
    //Here the thing
    if(n<.5 ^^ checker>0.) y=1.-y;
    col += y*mask;
    fragColor = vec4(col,1.0);
}

