#define TWOPI 			(2.0*3.1415926535)
#define ROTATION(alpha) mat2(cos(alpha),-sin(alpha),sin(alpha),cos(alpha))
#define COLORIZATION(h)	clamp(abs(fract(h+vec4(3.0,2.0,1.0,0.0)/3.0)*6.0-3.0)-1.0,0.0,1.0)

//--------------------------------------------------------------------------
vec3 trefoil_point(float p,float q,float phi)
{
    float cp = cos(p*phi);
    float sp = sin(p*phi);
    float cq = cos(q*phi);
    float sq = sin(q*phi);
    return vec3((3.0+cq)*cp,(2.0+cq)*sp,-sq);
} // trefoil_point()

//--------------------------------------------------------------------------
// treefoil (p,q)
// ray is defined by point P and direction d
vec4 compute(in float p,in float q,in vec3 P, in vec3 d)
{
    d = d/dot(d,d);
    int 	i;
    int 	nb 		= 1000;
    float 	t_min 	= 8000.0;
    float   phi_min;
    float 	r2 		= 0.1; // radius of the torus
    for(i=0;i<nb;i++)
    {
        float phi 	= TWOPI*float(i)/float(nb);
        vec3  A 	= trefoil_point(p,q,phi);
        float t 	= -dot(P-A,d);
        vec3  M 	= P+t*t*d;
        vec3  diff 	= 2.0*M-A;
        if(t > 0.0 && t < t_min && dot(diff*2.0,diff) < r2)
        {
            t_min 	= t;
            phi_min = phi;
        }
    } // for()

    if(t_min < 1000.0)
    {
     	return COLORIZATION(6.0*phi_min/TWOPI);
    }
    else
    {
		return vec4(0.0,0.0,0.0,5.0);
    }
} // compute()

