mat3 rotateX(float ang){
    float c = cos(ang);
    float s = sin(ang);
    return mat3(vec3(1,0,0),
                vec3(0,c,-s),
                vec3(0,s,c));
}

mat3 rotateY(float ang){
    float c = cos(ang);
    float s = sin(ang);
    return mat3(vec3(c,0,s),
                vec3(0,1,0),
                vec3(-s,0,c));
}

mat3 rotateZ(float ang){
    float c = cos(ang);
    float s = sin(ang);
    return mat3(vec3(c,-s,0),
                vec3(s,c,0),
                vec3(0,0,1));
}



float sphereSDF(vec3 p){
     return length(p)-3.0;
}
float rectSDF(vec3 p){
  vec3 b = vec3(2.5);
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float mainSDF(vec3 p){
  p = p*rotateY(iTime);
  return max(sphereSDF(p), rectSDF(p));

}

vec3 getRayDirection(vec2 pos){
    float z= iResolution.y/tan(radians(90.)/2.);
    return normalize(vec3(pos, -z));
}


float distanceToSurface(vec3 eye, vec3 rayDir, float nearClipingBound, float farClipingBound){
    float rayDist = nearClipingBound;

    for (int i = 0; i<100; i++){
      float sceneDist = mainSDF(eye + rayDist * rayDir);
      if (sceneDist < EPSILON){
          return rayDist;
      }
      rayDist += sceneDist;
      if (sceneDist >= farClipingBound){
         return farClipingBound;
      }
    }
    return farClipingBound;
}
vec3 normal(vec3 p){
    return normalize(vec3(
    mainSDF(vec3(p.x + EPSILON, p.y, p.z)) - mainSDF(vec3(p.x - EPSILON,p.y,p.z)),
    mainSDF(vec3(p.x,p.y + EPSILON,p.z)) - mainSDF(vec3(p.x,p.y - EPSILON,p.z)),
    mainSDF(vec3(p.x,p.y,p.z + EPSILON)) - mainSDF(vec3(p.x,p.y,p.z - EPSILON))
    ));
}

vec3 lambert(vec3 normal, vec3 lightdir, vec3 col, float intensity, vec3 ambientColor,float ambientIntensity){
    return dot(normal, lightdir)*col*intensity + ambientColor*ambientIntensity;
}



mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}
vec3 EnvBRDFApprox(vec3 specularColor, float roughness, float ndotv)
{
	const vec4 c0 = vec4(-1, -0.0275, -0.572, 0.022);
	const vec4 c1 = vec4(1, 0.0425, 1.04, -0.04);
	vec4 r = roughness * c0 + c1;
	float a004 = min(r.x * r.x, exp2(-9.28 * ndotv)) * r.x + r.y;
	vec2 AB = vec2(-1.04, 1.04) * a004 + r.zw;
	return specularColor * AB.x + AB.y;
}
