vec2 rotate2D(vec2 uv, float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c) * uv;
}

vec2 hash12(float t) {
    float x = fract(sin(t * 5.329));
    float y = fract(sin((t + x) * 5.732));
    return vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - vec2(0.45, 0.5) * iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.0);


    // tweak this to enlarge the entropy
    float r = 0.2;

    // dots iteration number
    for (float i=0.0; i < 120.0; i++) {
        float factor = (sin(iTime*.1) * 0.5 + 0.5);
        i += factor;

        float a = i / 3.;
        // This manipulates the dots spread in the x and y axis
        float dx = 1. * r * cos(a) - r * cos(8. * a);
        float dy = 1. * r * sin(a) - r * sin(8. * a);

        col += 0.013 * factor / length(uv - vec2(dx + 0.1, dy) - 0.02 * hash12(i));
    }
    col *= sin(vec3(0.2, 0.7, 0.9) * iTime*.5) * 0.15 + 0.25;

    fragColor = vec4(col, 1.0);
}

// This defines the base color based of the height.
vec3 palette( float t ) {
    vec3 a = vec3(0.428,0.588,0.488);
    vec3 b = vec3(0.308,-0.142,0.500);
    vec3 c = vec3(-0.422,-0.702,-0.382);
    vec3 d = vec3(0.548,-0.062,-0.472);

    return a + b*cos( 6.28318*(c*t+d) );
}


// The randomizer used is a very simple home-grown quadratic offset, seems to work well enough. It produces a random float from 0 - 2*PI and converts it o a vector.
// This type of RNG is used because for the same inputs it will create the same outputs, meaning we do not need to save a grid of random vectors, but can calculate them on the fly.
vec2 randomVector(int x, int y, float snapZoom) {

    int u = x + (y + 5) * int(snapZoom);

    float r = mod(float(u * (u + 1) * 42043), 6.283185307);

    return vec2(cos(r), sin(r));
}

// The interpolation function used is known as 'smootherstep', the second-order version of https://en.wikipedia.org/wiki/Smoothstep.
float interpolate(float a, float b, float f) {

    return (b - a) * ((f * (f * 6.0 - 15.0) + 10.0) * f * f * f) + a;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    uv.y += 0.1 * iTime;
    uv.x += 0.05 + 0.05 * sin(iTime * 0.3);

    // Offset so division by zero doesn't happen.
    float o = 0.00001;

    // The maximum zoom, so the definition of the highest octave.
    float maxZoom = (sin(iTime) + 1.1) * 512.0;

    float height = 0.0;

    for (float zoom = 2.0; zoom < maxZoom + 1.0; zoom *= 2.0) { 

        // Casting to an int is a simple way to create a grid. Floor would probably work too.
        int x = int(uv.x * zoom);
        int y = int(uv.y * zoom);

        // The vector pointing from the bottom right of the square to the point, also known as the local vector.
        vec2 loc = vec2(uv.x * zoom - float(x), uv.y * zoom - float(y));

        // Calculate the Dot product of the vector from the point to each corner, and the random vector at the corner.
        float bl = dot(vec2(o) - loc, randomVector(x, y, zoom));
        float br = dot(vec2(1.0 + o, o) - loc, randomVector(x + 1, y, zoom));
        float tl = dot(vec2(o, 1.0 + o) - loc, randomVector(x, y + 1, zoom));
        float tr = dot(vec2(1.0 + o, 1.0 + o) - loc, randomVector(x + 1, y + 1, zoom));

        // Interpolates a point between the bottom two points, and then the same for the top two.
        float b = interpolate(bl, br, uv.x * zoom - float(x));
        float t = interpolate(tl, tr, uv.x * zoom - float(x));

        // Using the square root of zoom creates the fractal effect.
        height += interpolate(b, t, uv.y * zoom - float(y)) / pow(zoom, 0.5);

    }

    fragColor = vec4(palette(height + 0.05), 0.0);


    // The following turns the noise into a simple map - skip if you just need the noise.

    // Water
    if (height < -0.07) {
        fragColor.z += 0.45;
        fragColor.y -= 0.2;
        fragColor /= 1.55;
    }
    // Shallows
    else if (height < 0.0) {
        fragColor.z += 0.5;
        fragColor.y -= 0.2;
        fragColor /= 1.1;
    }
    // Beach

    else if (height < 0.05) {
        fragColor.x += 0.3;
        fragColor.g += 0.15;
        fragColor.z += 0.15;
    }
    // Snow

    if (height > 0.3) {
        fragColor.xyz += 0.9;
    }
    // Mountains

    else if (height > 0.225) {
    fragColor.y -= 0.2;
    fragColor.x += 0.23;
    fragColor.z += 0.3;
    }
    // High forest

    else if (height > 0.11) {
    fragColor.y -= 0.2;
    fragColor.z += 0.1;
    }

}

float sdLineSquared(in vec2 iPoint, in vec2 iLine0, in vec2 iLine1) {
	vec2 diff = iPoint - iLine0;
	vec2 dir = iLine1 - iLine0;
	float t = dot(diff, dir) / (length(dir) * length(dir));
	t = clamp(t, 0.0, 1.0);
	vec2 oLinePoint = t * dir + iLine0;
	return length(iPoint - oLinePoint);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
