    uv2 -= .5;
    uv2 *= zoom;
    uv2 += .5;
    vec2 body1 = vec2(_(PX1), _(PY1));
    vec2 b1v = vec2(_(VX1), _(VY1));
    vec2 body2 = vec2(_(PX2), _(PY2));
    vec2 b2v = vec2(_(VX2), _(VY2));
    vec2 body3 = vec2(_(PX3), _(PY3));
    vec2 b3v = vec2(_(VX3), _(VY3));

    // Output to screen
    fragColor = texture(iChannel1, uv);
    //fragColor += texture(iChannel0, uv);

    float d1 = sdLineSquared(uv2, body1, body1 + b1v * 100.);
    fragColor.rgb += vec3(smoothstep(0.005, 0.001, d1));

    float d2 = sdLineSquared(uv2, body2, body2 + b2v * 100.);
    fragColor.rgb += vec3(smoothstep(0.005, 0.001, d2));

    float d3 = sdLineSquared(uv2, body3, body3 + b3v * 100.);
    fragColor.rgb += vec3(smoothstep(0.005, 0.001, d3));


    vec3 field = vec3(1., 0., 0.) * smoothstep(0., 100., 1./pow(length(uv2 - body1), 2.));
    field += vec3(1., 0., 1.) * smoothstep(0., 100., 1./pow(length(uv2 - body2), 2.));
    field += vec3(1., 1., 0.) * smoothstep(0., 100., 1./pow(length(uv2 - body3), 2.));


    fragColor.rgb += vec3(pow(field.x, 0.5), pow(field.y, 0.5), pow(field.z, 0.5));
}

#define SET(X,V) if (uv.x - 0.01 < X && uv.x + 0.01 > X) { fragColor = vec4(V, V, V, 1.); }
#define SETV(P,N,V) SET(PXN, (V).x) SET(PYN, (V).y)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float x1 = texture(iChannel0, vec2(PX1, 0.)).x;

    fragColor = vec4(0.0,0.0,0.0,1.0);

    if (iTime < 2. || iMouse.z > 0.) {
        SET(PX1, 1.)
        SET(PY1, 0.5)

        SET(VX1, -0.0005)
        SET(VY1, 0.000866)

        SET(PX2, 1.3)
        SET(PY2, 0.5 + 0.6 * 0.866);

        SET(VX2, 0.001)
        SET(VY2, 0.0000)

        SET(PX3, 1.6)
        SET(PY3, 0.5)

        SET(VX3, -0.0005)
        SET(VY3, -0.000866)
        return;
    }

    vec2 body1 = vec2(_(PX1), _(PY1));
    vec2 b1v = vec2(_(VX1), _(VY1));
    vec2 body2 = vec2(_(PX2), _(PY2));
    vec2 b2v = vec2(_(VX2), _(VY2));
    vec2 body3 = vec2(_(PX3), _(PY3));
    vec2 b3v = vec2(_(VX3), _(VY3));

    float G = 0.0000006;

    float D12 = length(body1 - body2);
    float F12 = G / (D12 * D12);
    float D13 = length(body1 - body3);
    float F13 = G / (D13 * D13);
    float D23 = length(body2 - body3);
    float F23 = G / (D23 * D23);

    b1v = normalize(body2 - body1) * F12 + b1v;
    b1v = normalize(body3 - body1) * F13 + b1v;

    b2v = normalize(body1 - body2) * F12 + b2v;
    b2v = normalize(body3 - body2) * F23 + b2v;

    b3v = normalize(body1 - body3) * F13 + b3v;
    b3v = normalize(body2 - body3) * F23 + b3v;

    body1 += b1v;
    body2 += b2v;
    body3 += b3v;


    SET(PX1, body1.x);
    SET(PY1, body1.y);
    SET(VX1, b1v.x);
    SET(VY1, b1v.y);

    SET(PX2, body2.x);
    SET(PY2, body2.y);
    SET(VX2, b2v.x);
