#define PI acos(-1.0)

// LINE FUNCTION
vec4 line(vec2 uv, vec2 A, vec2 B, vec3 col, float thic, float blur) {

    vec2 g = B - A;
    vec2 h = uv - A;
    float d;

    d = length(h - g * clamp(dot(g, h) / dot(g, g), 0.0, 1.0));
    if(blur != 0.)
        if((blur * thic) >= thic)
            d = smoothstep(blur * thic, thic, d);
        else
            d = smoothstep(thic, blur * thic * length(0.05), d);
    else
        d = step(thic, d);

    return vec4(col, d);
}

// CUBE FUNCTION
vec4 cube(
    vec2 uv,
    vec2 pos,
    float rot,
    float width,
    float height,
    float tilt,
    float thic,
    vec3 col
) {
    vec4 background, foreground;
    background = vec4(0.);

    for (int i = 0; i <= 3; i++) {
        float j = float(i);

        foreground = line(
            uv, 
            vec2(
                pos.x + sin(rot + PI/2. * j) * width * 1.5,
                pos.y + height - cos(rot + PI/2. * j) * tilt/45. * 100.
            ), 
            vec2(
                pos.x + sin(rot + PI/2. * j) * width * 1.5,
                pos.y - height - cos(rot + PI/2. * j) * tilt/45. * 100.
            ), 
            col,
            thic/10.,
            1.01
        );
        background = mix(background, foreground, foreground.a);
        foreground = line(
            uv, 
            vec2(
                pos.x + sin(rot + PI/2. * j) * width * 1.5,
                pos.y + height - cos(rot + PI/2. * j) * tilt/45. * 100.
            ), 
            vec2(
                pos.x + sin(rot + PI/2. * (j + 1.)) * width * 1.5,
                pos.y + height - cos(rot + PI/2. * (j + 1.)) * tilt/45. * 100.
            ), 
            col,
            thic/10.,
            1.01
        );
        background = mix(background, foreground, foreground.a);
        foreground = line(
            uv, 
            vec2(
                pos.x + sin(rot + PI/2. * j) * width * 1.5,
                pos.y - height - cos(rot + PI/2. * j) * tilt/45. * 100.
            ), 
            vec2(
                pos.x + sin(rot + PI/2. * (j + 1.)) * width * 1.5,
                pos.y - height - cos(rot + PI/2. * (j + 1.)) * tilt/45. * 100.
            ), 
            col,
            thic/10.,
            1.01
        );
        background = mix(background, foreground, foreground.a);
    }
    return background;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    
    // Normalized pixel coordinates (from 0 to 1)
