#define r2d(p,a) p=cos(a)*p + sin(a)*vec2(-p.y,p.x);

vec3 hash(vec3 p) {
  p = fract(p * vec3(443.537, 537.247, 247.428));
  p += dot(p, p.yxz + 19.19);
  return fract((p.xxy + p.yxx) * p.zyx);
} 


float cat(vec2 p) {
  p.x = abs(p.x);
  vec2 q=p;
  q.x = abs(q.x-.2);
  q.y += q.x - .2;
  float r = abs(q.y)<.05 && q.x<.15 ? 1. : 0.;
  p.x -= .6;
  p.y = abs(p.y) - .08;
  r += abs(p.y)<0.03 && abs(p.x)<.15 ? 1. : 0.;
  return r;
}

float boxDist(vec3 p, vec3 o, vec3 s, float r) {
  p = abs(p - o) - s / 2.;
  return length(max(p, 0.) + min(0., max(p.x, max(p.y, p.z)))) - r;
}

vec4 df(vec3 p) {
  float d = 1000.;
  //const vec3 cols[3] = {vec3(1,0,1), vec3(1,1,0), vec3(0,1,1) };
  p.z /= 3.;
  vec3 col;
  for (float i=0.; i<3.; i++) {
    float e = boxDist(p * vec3(1,1,0), vec3(sin(p.z + i * 3.142), cos(p.z + i * 3.142), 0.) * 1.5, vec3(.8), .1);
    if (e < d) {
      d = e;
      col = i==0. ? vec3(.7) : (i==1. ? vec3(.85) : vec3(1));
    }
    p.z *= -1.2;
  }

  d = min(
    d,
    -length(p.xy) + 2.
  );
  vec3 q = p;
  r2d(q.xy, q.z / 3.);
  if (d > 0.1) {
    p = .5 - abs(max(fract(p), fract(q)) - .5);
    //col = step(0.98, fract(p + fract(q))) * 8.;
    col = 1. - step(.01, p);
    col = vec3(max(col.x, max(col.y, col.z))) * 4.;
  }
  return vec4(col, d);
}

vec3 norm(vec3 p) {
  vec2 e = vec2(1e-3, 0.);
  return normalize(vec3(
    df(p + e.xyy).w - df(p - e.xyy).w,
    df(p + e.yxy).w - df(p - e.yxy).w,
    df(p + e.yyx).w - df(p - e.yyx).w
  ));
}

vec3 rm(vec3 p, vec3 dir, float mDist) {
  vec3 col = vec3(0.);
  float td = 0.;
  for (int i=0; i< 20; i++) {
    vec4 d = df(p);
    td += d.w;
    if (d.w < 1e-3 || td > mDist) {
      col = d.xyz;
      vec3 n = norm(p);
      col *= pow(max(0., dot(-n, dir)), 2.);
      break;
    }
    p += d.w * dir;
  }
  return col;
}

const int samples = 16;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
  //vec2 uv = fragCoord/iResolution.xy;
  vec2 uv = vec2(fragCoord.xy * 2. - iResolution.xy) / iResolution.yy;

  vec3 col = vec3(0.);

  for (int j=0;j<samples; j++) {
    vec3 k = hash(vec3(uv, fract(iTime + float(j) / float(samples)))) - .5;
    k.z = float(j) / float(samples);
    float t = iTime + k.z / 15.;
    vec2 c = vec2(fragCoord.xy) + k.xy * 2.; //texture(texFFTSmoothed, 0.01).x * 20.;
    vec2 u = vec2(c * 2. - iResolution.xy) / iResolution.yy;
    vec3 p = vec3(0, 0, t * 10.),
    dir = normalize(vec3(u, .5));
    r2d(dir.xy, t / 4.);
    r2d(dir.xz, sin(t / 20.47) * 2.);
    r2d(dir.yz, sin(t / 17.24) * 2.);

    float d = 1. / length(dir.xy), d2 = d * 2.;
    p += dir * d;

    int idx = int((k.z) * 3.);
    col[idx] += rm(p, dir, d / 2.).r * 3.;
    //col += hash(floor(p * 10.)) * .5 + .25;
  }

  vec3 catC = vec3(0);
  for (float i=0.;i<9.;i++) {
    vec2 o = vec2(
        sin(i / 10. + iTime * 2.347),
        sin(i / 10. + iTime * 2.789)
        );
    o = pow(abs(o), vec2(7.)) * sign(o);
    catC[int(i)/3] += cat((uv + o / 4.) * .5) / 3.;
  }
	fragColor = vec4(col / float(samples) + catC, 1.);
    //fragColor = vec4(col,1.0);
}

#define PI 3.14159265

float circle( vec2 uv, vec2 pos, float rad) {
    uv -= pos;
    return smoothstep(rad-3.0, rad-1.0, length(uv)) -
           smoothstep(rad+1.0, rad+3.0, length(uv));
}

float fillCircle( vec2 uv, vec2 pos, float rad) {
    uv -= pos;
    return smoothstep(rad-1.0, rad-3.0, length(uv));
}

float line(vec2 uv, vec2 start, float len, float spinAng) {
    vec2 l = uv - start;
    float ang = mod(atan(l.y, l.x), 2.0*PI);
    float dis = length(l)/len;
    return step(-len, -length(l)) * 
           (smoothstep(spinAng-0.03*(1.2-dis), spinAng, ang) - 
            smoothstep(spinAng, spinAng+0.03*(1.2-dis), ang));
}

float lineShadow(vec2 uv, vec2 start, float len, float spinAng) {
    vec2 l = uv - start;
    float ang = mod(atan(l.y, l.x), 2.0*PI);
    return step(-len, -length(l)) *
           (smoothstep(spinAng-PI/3.0, spinAng, ang) +
            smoothstep(spinAng-PI/3.0+2.0*PI, spinAng+2.0*PI, ang) -
            step(spinAng, mod(atan(l.y, l.x), 2.0*PI))
           );
}

float target(vec2 uv, vec2 center, vec2 pos, float spinAngle) {
    vec2 taPos = pos - center;
    float delta = mod(spinAngle - atan(taPos.y, taPos.x), 2.0*PI)/PI;
    float color = fillCircle(uv, pos, 10.0);
    color += circle(uv, pos, 20.0*delta+10.0);
    return clamp(color * (1.0-delta), 0.0, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;
    vec2 center = iResolution.xy/2.0;

    float outer = circle(uv, center, 200.0);
    float inner = fillCircle(uv, center, sin(iTime)*1.0+5.0);

    float spinAngle = mod(iTime*2.0, 2.0*PI);
    float spin = lineShadow(uv, center, 200.0, spinAngle) * 0.3;
    spin += line(uv, center, 200.0, spinAngle);

    float targets = target(uv, center, center + vec2(120.0, 60.0), spinAngle);
    targets += target(uv, center, center + vec2(-50.0, -150.0), spinAngle);
    targets += target(uv, center, center + vec2(-90.0, 100.0), spinAngle);

    float color = outer + inner + spin + targets; 
    fragColor = vec4(vec3(color), 1.0);
}

