//@iq box and extrude
float opx( in vec3 p, float d, in float h )
{
    vec2 w = vec2( d, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float box( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float text_(vec2 p){
    return box(p,vec2(.275,.275));
}

float text_n(vec2 p){
    float d = box(vec2(abs(p.x)-.17,p.y),vec2(.08,.275));
    vec2 pv = p*r56;
    float e = box(pv,vec2(.075,.325));

    return max(min(d, e),text_(p));
}

float text_z(vec2 p){
    float d = box(p-vec2(-.045,.25),vec2(.2,.115));
    d = min(d,box(p+vec2(-.045,.25),vec2(.2,.115)) );
    vec2 pv = p*r56n;
    float e = box(pv,vec2(.075,.375));

    return max(min(d, e),text_(p));
}

float text_x(vec2 p){
    vec2 pv = p*r56n;
    float d = box(pv,vec2(.08,.4));

    pv = p*r56;
    float e = box(pv,vec2(.08,.4));

    return max(min(d, e),text_(p));
}

float text_t(vec2 p){
    float d = box(p,vec2(.085,.275));
    float e = box(p-vec2(0,.25),vec2(.275,.115));

    return max(min(d, e),text_(p));
}

float swtch;

vec2 map (in vec3 p) {
    vec2 res = vec2(1e5,-1.);

    vec3 q = p;

    float tt,id;

    if(swtch <1.) {
        float size = 3.,hlf = size/2.;

        tt = mod(T*.1,1.);
        p+=T*(hlf/5.);
        id = floor((p.x+hlf)/size)*.5;

        q.x+=tt*size;
        q.x = mod(q.x+hlf,size)-hlf;
    } else {
        float size = 1.5,hlf = size/2.;

        tt = mod(T*.25,1.);
        p+=T*(hlf/4.);

        q.y+=tt*size;
        q.y = mod(q.y+hlf,size)-hlf;
    }

    float d = text_n(q.xy+vec2(.90,0));
    d = min(d,text_z(q.xy+vec2(.30,0)));
    d = min(d,text_x(q.xy-vec2(.30,0)));
    d = min(d,text_t(q.xy-vec2(.90,0)));

    float fd = opx(q,d,.18);

    float e = opx(q,abs(d-.02)-.0085,.15);

    if(fd<res.x) {
        res=vec2(fd*.8,1);
        hit=p;
    }
    if(e<res.x&&mod(id,1.)==0.) {
        res=vec2(e*.75,2);
        hit=p;
    }
	return res;
}

//Tetrahedron technique
//https://iquilezles.org/articles/normalsSDF
vec3 normal(vec3 p, float t, float mindist)
{
    float e = mindist*t;
    vec2 h = vec2(1.,-1.)*.5773;
    return normalize( h.xyy*map( p + h.xyy*e ).x + 
					  h.yyx*map( p + h.yyx*e ).x + 
					  h.yxy*map( p + h.yxy*e ).x + 
					  h.xxx*map( p + h.xxx*e ).x );
}

vec3 shade(vec3 p, vec3 rd, float d, float m, inout vec3 n)
{
    vec3 clr;
    n = normal(p,d,1.);
    vec3 lpos = vec3(.1,3,2);
    vec3 l = normalize(lpos-p);

    float diff = clamp(dot(n,l),0.,1.);
    float fresnel = pow(clamp(1.+dot(rd, n), 0., 1.), 5.5);
    fresnel = mix(.01, .7, fresnel);

    float cpx = swtch<1. ? hit.x : hit.y;
    if(m==1.) clr = .5 + .4 *sin(cpx*.75+ vec3(2.5,1.,.5));
    if(m==2.) clr = .5 + .4 *sin((cpx+15.25)*1.+ vec3(2.5,1.,.5));

    vec3 h = mix(vec3(0),clr,diff);

    return h;
}


vec3 renderAll( in vec2 F )
{   
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);

    vec3 C=vec3(.0);

    uv*=rot(.48);
    vec3 ro = vec3(0,0,2),
         rd = normalize(vec3(uv,-1));

    float x = M.xy == vec2(0) || M.z <0. ? 0. :  (M.y/R.y * .3 - .15) * PI;
    float y = M.xy == vec2(0) || M.z <0. ? 0. : -(M.x/R.x * .3 - .15) * PI;

    rx = rot(-.48+x);
    ry = rot(y);

    ro.yz *= rx, ro.xz *= ry;
    rd.yz *= rx, rd.xz *= ry;

    vec3  p = ro + rd * .1;
    float atten = .75;
    float k = 1.;
    float b = 5.;
    float iv = 1.;

    // loop inspired/adapted from @blackle's 
    // marcher https://www.shadertoy.com/view/flsGDH
    for(int i=0;i<132;i++)
    {
        vec2 ray = map(p);
        vec3 n=vec3(0);
        float fresnel=0.;
        float d = ray.x;
        float m = ray.y;

        p += rd * d *k;

        if (d*d < 1e-9) {

            C+=shade(p,rd,d,ray.y,n)*atten;
            if(b<0.)break;

            atten *= .65;
            p += rd*.0001;
            k = sign(map(p).x);

            b--;

            p += rd*.005;
            k = sign(map(p).x);

            vec3 rf=refract(rd,n,iv > 0. ? 1./1.1 : 1.075);
            iv *= -1.;
            if(length(rf) == 0.) rf = reflect(rd,n);
            rd=rf;
            p+=-n*.001;

        }  


        if(distance(p,rd)>50.) { break; }
    }
    return C;

}


void mainImage( out vec4 O, in vec2 F )
{   


    swtch = mod(T*.08,2.);
    r56 = rot(.56);
    r56n = rot(-.56);

    vec3 C = renderAll(F);
    C +=renderAll(F+vec2(.5,.5));
    C /= 1.5;    

    O = vec4(sqrt(smoothstep(0.,1.,C)),1.0);
}
