const vec2 size = vec2(3.,1.75);
const vec2 hlf = size/2.;

vec2 map(vec3 p) {
    vec2 res = vec2(1e5,0);

    vec3 q = (p-vec3(0,.35,0))*(.5);

    float d = text_n(q.xy+vec2(.90,0));
    d = min(d,text_z(q.xy+vec2(.30,0)));
    d = min(d,text_x(q.xy-vec2(.30,0)));
    d = min(d,text_t(q.xy-vec2(.90,0)));

    d = opx(q,d,.15);

    if(d<res.x) {
        res=vec2(d,1);
        hit=p;
    }

    float f = p.y+1.;
    if(f<res.x) {
        res=vec2(f,2);
        hit=p;
    }
    return res;
}

vec3 normal(vec3 p, float t)
{
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec3 FC = vec3(0.071,0.016,0.106);

vec3 tile(vec3 hp){

    vec2 id   = floor(hp.xz*3.);
    vec2 grid = fract(hp.xz*3.)-.5;

    float ns = hash21(id);
    float px = fwidth(hp.x);
    float sz = sampleFreq(ns);

    vec3 clr = .5 + .4 *sin((12.+ns)*2.75+ vec3(2.5,1.,.5));
    float df = box(grid,vec2(sz*.45))-.075;
    vec3 c = sz>.25 ? mix(vec3(0),clr,smoothstep(px,-px,df)) :vec3(0);

    return c;
}
vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, bool last, inout float d) {

    vec3 C = vec3(0);
    float m = 0.;
    vec3 p = ro;

    for(int i=0;i<128;i++) {
        p = ro + rd * d;
        vec2 ray = map(p);
        if(ray.x<MIN_DIST*d||d>MAX_DIST)break;
        d += ray.x;
        m  = ray.y;
    } 
    hp = hit;

    float alpha = 0.;
    if(d<MAX_DIST)
    {
        vec3 n = normal(p,d);
        vec3 lpos =  vec3(12,8,3);
        vec3 l = normalize(lpos-p);

        float diff = clamp(dot(n,l),0.,1.);

        vec3 h = vec3(.05);

        if(m==1.) {
            h= .45 + .4 *sin((hp.y+48.5)*5.25+ vec3(2.5,1.,.5));
            ref=h*.65;
        }
        if(m==2.) {
            hp.z+=T*2.15;
            h = tile(hp);
            ref = vec3(.35);
         }

        C = h*diff;
        C = mix(FC,C,  exp(-.00005*d*d*d));

        ro = p+n*.001;
        rd = reflect(rd,n);

    }else{
        C = FC;
    } 
    return vec4(C,alpha);
}
