void mainImage( out vec4 O, in vec2 F )
{
    r56 = rot(.56);
    r56n = rot(-.56);
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);

    FC = .5 + .4 *sin((uv.y+47.)*2.75+ vec3(2.5,1.,.5));
    vec3 ro = vec3(0,0,4.);
    vec3 rd = normalize(vec3(uv, -1));

    float x = M.xy==vec2(0) || M.z<0. ? 0. : (M.y / R.y * .2 - .1) * PI;
    float y = M.xy==vec2(0) || M.z<0. ? 0. : (M.x / R.x * .2 - .1) * PI2;

    mat2 rx = rot(.28+x-.2*cos(T*.12)), ry = rot(y+.3*sin(T*.25));
    ro.yz *= rx,ro.xz *= ry;
    rd.yz *= rx,rd.xz *= ry;

    vec3 C = vec3(0),ref=vec3(0),fil=vec3(1);

    float d = 0.,a = 0.;
    float numBounces = 3.;

    for(float i=0.; i<numBounces; i++) {
        d = 0.;
        vec4 pass = render(ro, rd, ref, i==numBounces-1., d);
        C += pass.rgb*fil;
        fil*=ref;
        if(i==0.) a = d;
    }

    C = mix(C,clamp(C+.025,C,vec3(1)),hash21(uv));
    C = mix(FC,C,  exp(-.0015*a*a*a));

    C = pow(C, vec3(.4545));
    O = vec4(C,1.0);
}

