}

// Two ways how to fold 2D coordinates into hexagonal cells.
// Both are commonly used of course, but I had fun working them out. ;)

// The folded coordinates have [0, 0] in the center of the cell.
// You also get the 2D integer cell index.

// To change the cell size: p_folded = hexfold(p / size, cell_index) * size


// Method 1: cluster of six triangles.
vec2 hexfold(vec2 p, out vec2 cell_index) {
    // Convert to triangular coordinates: → x [2, 0], ↖ y [-1, √3].
    // The cell index will be in the dual basis: ↗ x* [0.5, 0.5/√3], ↑ y* [0, 1/√3].
    // (The dual basis has dot(x*,x) = dot(y*,y) = 1, dot(x*,y) = dot(y*,x) = 0.)
    vec4 M = vec4(2,-1, 0,sqrt(3.));
    vec2 s = mat2(M) * p;

    // Take six triangles and combine them into a hexagon.
    // Division by 3 is not exact, so we need to avoid edge cases when rounding.
    // These are all OK:
    //cell_index = round((floor(s) + ceil(s.x+s.y)) / 3.);  // 0 0.33 | 0.66
    //cell_index = round((ceil(s) + floor(s.x+s.y)) / 3.);
    //cell_index = round((floor(s) + floor(s.x+s.y) + 1.) / 3.);
    //cell_index = floor((ceil(s) + ceil(s.x+s.y) + .5) / 3.);  // | 0.16 0.5 0.83 |
    cell_index = floor((floor(s) + floor(s.x+s.y) + 2.5) / 3.);

    // Transform the cell indices back and fold the coordinates.
    return p - .5*mat2(M.xzyw) * cell_index;  // transpose(M) manually for WebGL 1
}

// Method 2: two stretched rectangular grids.
vec2 hexfold2(vec2 p, out vec2 cell_index) {
    // Convert to stretched rectangular coordinates.
    vec2 s = vec2(1, sqrt(3.));

    // Duplicate the grid with a [0.5, 0.5] offset.
    vec2 i = round(p/s), j = round(p/s + .5) - .5;
    vec2 q = p - i*s, r = p - j*s;

    // Find the closest cell center.
    cell_index = round(dot(q,q)<dot(r,r) ? i+i : j+j);  // integer
    return dot(q,q)<dot(r,r) ? q : r;
}

// Golfed versions of the above.
// These return the folded coordinates in .xy and the cell index in .zw.
vec4 hexfold_vec4(vec2 p) {
    vec4 M = vec4(2,-1, 0,sqrt(3.));
    vec2 s = mat2(M) * p,
         i = round(floor(s + ceil(s.x+s.y)) / 3.);
    return vec4(p - .5*mat2(M.xzyw) * i, i);
}

vec4 hexfold2_vec4(vec2 p) {
    vec2 s = vec2(1,sqrt(3.)),
         i = round(p/s), j = round(p/s + .5) - .5,
         q = p-i*s, r = p-j*s;
    return dot(q,q)<dot(r,r) ? vec4(q,round(i+i)) : vec4(r,round(j+j));
}

// If you want just the cell index of method 1, you don't need the matrix.
// Method 2 works with distances, so it can't be much shorter.
vec2 hexindex(vec2 r) {
    vec2 s = vec2(2.*r.x, sqrt(3.)*r.y-r.x);
    return round(floor(s + ceil(s.x+s.y)) / 3.);
}

// Square fold for comparison.
// It also has 0 in the center of the cell.
vec2 squarefold(vec2 p, out vec2 cell_index) {
    cell_index = round(p);
    return p - cell_index;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = 4. * (2.*fragCoord - iResolution.xy) / iResolution.y;  // y: -4 .. 4
    vec2 p_folded, cell_index;

    float t = mod(iTime/2., 6.);

    // 0..3: Show the full versions to compare with the square fold.
    if      (t < 1.) p_folded = hexfold(p, cell_index);
    else if (t < 2.) p_folded = hexfold2(p, cell_index);
    else if (t < 3.) p_folded = squarefold(p, cell_index);

    // 4..5: Show the golfed versions.
    else if (t < 4.) { vec4 v = hexfold_vec4(p); p_folded = v.xy; cell_index = v.zw; }
    else if (t < 5.) { vec4 v = hexfold2_vec4(p); p_folded = v.xy; cell_index = v.zw; }

    // 6..7: Show how the cell indices are oriented.
    else {
        if (fract(t) < .5) {
            vec2 h = hexindex(p);
            // Method 1 gives you cell indices with Y vertical and X 60° clockwise from it.
            // Even directions on the clock: 12 y, 2 x, 4 x-y, 6 -y, 8 -x, 10 y-x.
            // Add neighbors to get odd directions: 3 x+x-y.
            fragColor = .5 + .07 * vec4(.4*(h.y-h.x - h.x), h.y, .4*(h.x + h.x-h.y),0); }  // 9, 12, 3
        else {
            // Method 2 has Y vertical and X is 90° clockwise: 12 y, 3 x, 6 -y, 9 -x.
            vec2 h = hexfold2_vec4(p).zw;
            fragColor = .5 + .07 * vec4(.4*-h.x, h.y, .4*h.x,0); }  // 9, 12, 3
    }

    if (t < 5.) {
        // Show cell indices mod 4 (red+green) and local coordinates (blue).
        vec2  index = mod(cell_index, 4.);
        float angle = fract(atan(p_folded.x, p_folded.y) / 6.283 - t);
        fragColor = pow(vec4(index*.3, angle*.6, 1), vec4(1./2.2));
    }
}

