vec2 raymarch (in vec3 ro, in vec3 rd) {
    vec2 h = vec2(0.001, 0.);
    float t = 0.;
    float tmax = 100.;

    for (int i = 0; i < 100; i++){
        if (abs(h.x) < 0.001*t || t > tmax) break;
        h = map(ro + t * rd);
        t += 0.25 * h.x;
    }

    if(t > tmax) h.y = -1.;

    return vec2(t, h.y);
}

