#define MAX_ITERATION 100

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // fit to mandelbrot scale
    vec2 c = vec2((uv.x * 3.5) - 2.5, (uv.y * 2.0) - 1.0);

    // fit to custom scale
    c /= 10.0;
    c.x -= 26.0 / 16.0;
    c.y = -c.y; // flip y
    c.y -= 0.5 / 16.0;

    vec2 z = vec2(0.0);
    int iteration;
    for (iteration = 0; iteration < MAX_ITERATION; iteration++)
    {
        z = vec2(z.x * z.x - z.y * z.y + c.x, 2.0 * abs(z.x * z.y) + c.y);
        if (length(z) > 4.0)
            break;
    }

    vec3 col = vec3(0.0);
    if (iteration < MAX_ITERATION)
    {
        float f0 = float(iteration) / float(MAX_ITERATION);
        float f1 = max(0.0, mix(0.4 - f0, 0.1 - f0, uv.y));
        col = vec3(f0, f1 * 0.8, f1);
    }

    fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 70.
#define MAX_DIST 14.
#define DIFF_EPS 0.002

#define SPEED 0.1
#define POINTS 20.
#define LENGTH 0.9
#define RADIUS 0.6
#define FADING 0.6
#define M_PI   3.1415926
#define M_2_PI 6.2831853
#define M_PI_2 1.5707963

/* quaternions */

vec4 qmult(vec4 p, vec4 q) {
    vec3 pv = p.xyz, qv = q.xyz;
    return vec4(p.w * qv + q.w * pv + cross(pv, qv), p.w * q.w - dot(pv, qv));
}

vec4 qrotor(vec3 axis, float phi) {
    return vec4(sin(phi*0.5) * axis, cos(phi*0.5));
}

vec4 qmouse(vec4 iMouse, vec3 iResolution, float iTime, float initRotation) {
    vec2 init = vec2(0.5 + 0.25*initRotation * sin(iTime), 0.5 + initRotation * cos(iTime));
    vec2 mouse = mix(init, iMouse.xy / iResolution.xy, step(0.0027, iMouse.y));
    vec4 rotY = qrotor(vec3(0., 1., 0.), M_PI - M_2_PI * mouse.x);
    vec4 rotX = qrotor(vec3(1., 0., 0.), M_PI * mouse.y - M_PI_2);
    return qmult(rotY, rotX);
}

vec3 rotate(vec3 point, vec4 qrotor) {
    vec3 rv = qrotor.xyz;
    return qmult(qrotor, vec4(point * qrotor.w - cross(point, rv), dot(point, rv))).xyz;
}


// optimized quadratic bezier https://www.shadertoy.com/view/ldj3Wh
vec2 sdBezier(vec3 pos, vec3 A, vec3 B, vec3 C) {    
    vec3 a = B - A;
    vec3 b = A - 2.0*B + C;
    vec3 c = a * 2.0;
    vec3 d = A - pos;

    float kk = 1.0 / dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);

    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;

    h = sqrt(h);
    vec2 x = (vec2(h, -h) - q) / 2.0;
    vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
    float t = clamp(uv.x+uv.y-kx, 0.0, 1.);
    return vec2(length(d+(c+b*t)*t),t);
}

vec3 trefoil(float t) {
    return vec3(sin(t) + 2. * sin(2. * t), cos(t) - 2. * cos(2. * t), -sin(3. * t));
}

float map(vec3 pos) {
    float t = fract(SPEED * iTime);
    float dl = LENGTH / POINTS;
    vec3 p1 = trefoil(t * M_2_PI);
    vec3 p2 = trefoil((dl + t) * M_2_PI);
    vec3 c = (p1 + p2) / 2.0;
    float d = 1e9;

    for(float i = 2.0; i < POINTS; i++){
        p1 = p2;
        p2 = trefoil((i * dl + t) * M_2_PI);
        vec3 c_prev = c;
        c = (p1 + p2) / 2.;
        vec2 f = sdBezier(pos, c_prev, p1, c);
        d = min(d, f.x + FADING * (1. - (f.y + i) / POINTS));
    }
    return d - RADIUS;
}

vec3 normal(vec3 pos) {
    const vec2 e = vec2(DIFF_EPS, 0.);
    return normalize(vec3(  	map(pos + e.xyy) - map(pos - e.xyy),
              				  	map(pos + e.yxy) - map(pos - e.yxy),
              					map(pos + e.yyx) - map(pos - e.yyx)));
}

// fast glow https://www.shadertoy.com/view/3dcXWX 
vec2 marchTrefoil(vec3 camera, vec3 dir) {
    float I, dt, t = 0.;
    vec3 pos = camera + t * dir;
    for(float i = 0.; i < MAX_STEPS; i++) {
      	pos = camera + t * dir;
        dt = 0.8 * map(pos);
        t += dt;
        I = i;
        if(dt < DIFF_EPS || t > MAX_DIST) break;
    }
    float outer = step(MAX_DIST, t);  
    float k = 1. + dot(normal(pos), dir);
    I -= mix(clamp((0.5 + 1. * k) * log2(DIFF_EPS/dt), .0, 1.), 1.2 * log2(dt) - 2.8, outer);
    return vec2(I/23., outer);
}

vec2 sphereCubemap(vec3 camera, vec3 dir, float radius) {
    float k = dot(camera, dir);
    float c = dot(camera, camera) - radius * radius;
    float D = k * k - c; 
    float s = -k + sqrt(D);
    vec3 d = normalize(camera + s * dir);

    vec3 n = abs(d);
    vec3 v = (n.x>n.y && n.x>n.z) ? d.xyz: (n.y>n.x && n.y>n.z) ? d.yzx: d.zxy;
    vec2 q = v.yz/v.x;
    q *= 1.25 - 0.25*q*q;
    return 0.5 + 0.5 * q;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;    
    vec3 dir = normalize(vec3(uv, -2.));
    vec3 camera = vec3(0., -0.2, 8.);

    vec4 mouse = qmouse(iMouse, iResolution, 0.666*iTime+5., 0.02);
    camera = rotate(camera, mouse);
    dir = rotate(dir, mouse);

    vec2 glow = marchTrefoil(camera, dir);
    vec2 suv = sphereCubemap(camera, dir, 1e4);

    vec3 noize = step(0.1, texture(iChannel0, suv).x) + texture(iChannel1, suv).xyz ;
    vec3 space = vec3(step(0.046, length(noize - vec3(0.05)))) * (noize - 1.);

    vec3 col =  (vec3(.0, .0, .015) + 0.03 * max(space, 0.)) * glow.y;
    col = col + mix(vec3(0., 0.25, 0.6), col, length(uv)) * glow.y;
    col += mix(vec3(.0, 0., .35), vec3(0.87, 0.0, 0.69), glow.x);
    col += vec3(0.1, 0.2, .1) * glow.x*(1.-glow.x);

    fragColor = vec4(pow(col, vec3(2.5)), 0.30);
}

