#define N 128.

#define MAX_T 10.
#define GOLDEN 1.618
#define PI 3.141

// the ith point out of n spherical fibonacci points
// these are mapped in the 2d space of the sphere
// returns (phi, theta) to be used with pointOnSphere
vec2 sfi(float i, float n) {
    float phi = 2. * PI * fract(i / GOLDEN);
    float z = 1. - (2. * i + 1.) / n;
    return vec2(phi, acos(z));
}

vec3 pointOnSphere(float phi, float theta) {
    return vec3(cos(phi) * sin(theta), sin(phi) * sin(theta), cos(theta));
}

float sphere(vec3 p, float rad) {
    return length(p) - rad;
}

float map(vec3 p) {
    float nearest = MAX_T;
    for (float i = 0.; i < N; i++) {
        vec2 sp = sfi(i, N);
        // over time, move points along the spiral
        sp += iTime;
        vec3 sp3 = pointOnSphere(sp.x, sp.y);
        nearest = min(nearest, sphere(p - sp3, .01));
    }
    return nearest;
}

mat3 cam(vec2 direction) {
    vec3 z = normalize(vec3(direction, 1.));
    vec3 x = normalize(cross(vec3(0., 1., 0.), z));
    vec3 y = cross(z,x);
    return mat3(x,y,z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2. - iResolution.xy)/iResolution.y;

    vec2 rotp = iMouse.xy / iResolution.xy - 0.5;
    mat3 rot = cam(rotp * 5.);

    vec3 ro = rot * vec3(0., 0., 0.0);
    vec3 rd = rot * normalize(vec3(uv, -1.));

    vec3 col = vec3(0.02);
    float stepCount = 0.;
    for(float t = 0.; t < MAX_T; stepCount++) {
        vec3 p = ro + rd * t;
        float nearest = map(p);
        if (nearest < .001) {
            col = vec3(1.);
            break;
        } else {
            col = vec3(stepCount/2., stepCount/4., stepCount) / 100.;
            t += nearest;
        }
    }

    // Output to screen
    vec3 prev = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;
    vec3 cur = (col + prev) * .95;
    fragColor = vec4(cur, 1.);
}

//--------------------------------------------------------------------------
#define TWOPI 			(2.0*3.1415926535)
#define ROTATION(alpha) mat2(cos(alpha),-sin(alpha),sin(alpha),cos(alpha))
#define COLORIZATION(h)	clamp(abs(fract(h+vec4(3.0,2.0,1.0,0.0)/3.0)*6.0-3.0)-1.0,0.0,1.0)

//--------------------------------------------------------------------------
vec3 trefoil_point(float p,float q,float phi)
{
    float cp = cos(p*phi);
    float sp = sin(p*phi);
    float cq = cos(q*phi);
    float sq = sin(q*phi);
    return vec3((3.0+cq)*cp,(2.0+cq)*sp,-sq);
} // trefoil_point()

//--------------------------------------------------------------------------
// treefoil (p,q)
// ray is defined by point P and direction d
vec4 compute(in float p,in float q,in vec3 P, in vec3 d)
{
    d = d/dot(d,d);
    int 	i;
    int 	nb 		= 1000;
    float 	t_min 	= 8000.0;
    float   phi_min;
    float 	r2 		= 0.1; // radius of the torus
    for(i=0;i<nb;i++)
    {
        float phi 	= TWOPI*float(i)/float(nb);
        vec3  A 	= trefoil_point(p,q,phi);
        float t 	= -dot(P-A,d);
        vec3  M 	= P+t*t*d;
        vec3  diff 	= 2.0*M-A;
        if(t > 0.0 && t < t_min && dot(diff*2.0,diff) < r2)
        {
            t_min 	= t;
            phi_min = phi;
        }
    } // for()

    if(t_min < 1000.0)
    {
     	return COLORIZATION(6.0*phi_min/TWOPI);
    }
    else
    {
		return vec4(0.0,0.0,0.0,5.0);
    }
} // compute()

