void mainImage(out vec4 fragColor,vec2 fragCoord)
{
    float m 		= min(iResolution.x,iResolution.y);

                // center posistion and  cam zoom,
	vec2 uv 		= sin(0.25*iTime)*(fragCoord.xy / m - 2.0*vec2(0.454,0.25))*15.0; //cam zoom,

    vec3 P 			= vec3(uv*0.75,-2.0);
    vec3 d      	= vec3(0.0,0.0,1.0);
            //  speed cam rotation,
    float alpha1	= iTime*TWOPI/8.;
    float alpha2	= iTime*TWOPI/1.5;

    P.zx		   *= ROTATION(alpha1);
    P.xy		   *= ROTATION(alpha2);
    d.zx 		   *= ROTATION(alpha1);
    d.xy		   *= ROTATION(alpha2);


    float i = mod(iTime/2.0 /* change every 3 seconds */,16.0);
    float p,q;
    if(i <= 1.0) 		{p=6.0;q=1.0;}
