    vec3 col = texture( iChannel0, uv ).xyz;
    col /= texture( iChannel0, uv ).w;

    //tone mapping
    col = reinhard(col);


    fragColor = vec4(col, 1.0 );
}

#define PI atan(1.0)*4.0

///// COMMUN /////

float saturate (float x)
{
    return min(1.0, max(0.0,x));
}
vec3 saturate (vec3 x)
{
    return min(vec3(1.,1.,1.), max(vec3(0.,0.,0.),x));
}

mat2 rot(in float a)
{
    a *= 0.0174533;
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

///// STRUCT /////

struct hitPayload
{
    vec3 orig;
    vec3 hitPos;
    vec3 dir;
    vec3 normal;
