// Simulate the pitch inputs using a smoothed stairs function STEP
#define STEP1(x) ((x) - sin(x))
#define STEP(x, offset, amp) (STEP1(STEP1(offset + x * amp)) * .15)
#define is (iTime)

float pitches[9];
void genPitches() {
  pitches[0] = STEP(is, 1., 1.);
  pitches[1] = STEP(is, 2., 2.);
  pitches[2] = STEP(is, 3., 3.);
  pitches[3] = STEP(is, 4., 4.);
  pitches[4] = STEP(is, 5., 1.);
  pitches[5] = STEP(is, 6., 2.);
  pitches[6] = STEP(is, 7., 3.);
  pitches[7] = STEP(is, 8., 4.);
  pitches[8] = STEP(is, 1., 5.);
}
#define ipitch_1 pitches[0]
#define ipitch_2 pitches[1]
#define ipitch_3 pitches[2]
#define ipitch_4 pitches[3]
#define ipitch_5 pitches[4]
#define ipitch_6 pitches[5]
#define ipitch_7 pitches[6]
#define ipitch_8 pitches[7]
#define ipitch_9 pitches[8]

#define icolor (iTime*.5)
#define imoveFWD (iTime*.1)
#endif


// CC0: Complicating things even further
//  That is what I do.

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define PI_2        (0.5*PI)
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define PCOS(a)     0.5*(cos(a)+1.0)

const float planeDist = 1.0-0.75;
const int   furthest  = 12;
const int   fadeFrom  = max(furthest-4, 0);
const float fadeDist  = planeDist*float(furthest - fadeFrom);

// License: Unknown, author: Unknown, found: don't remember
vec4 alphaBlend(vec4 back, vec4 front) {
  float w = front.w + back.w*(1.0-front.w);
  vec3 xyz = (front.xyz*front.w + back.xyz*back.w*(1.0-front.w))/w;
  return w > 0.0 ? vec4(xyz, w) : vec4(0.0);
}

// License: Unknown, author: Unknown, found: don't remember
vec3 alphaBlend(vec3 back, vec4 front) {
  return mix(back, front.xyz, front.w);
}

// License: Unknown, author: Unknown, found: don't remember
float tanh_approx(float x) {
  //  Found this somewhere on the interwebs
  //  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

vec3 offset(float z) {
  float a = z*0.5;
  vec2 p = -0.25*(vec2(cos(a), sin(a*sqrt(2.0))) + vec2(cos(a*sqrt(0.75)), sin(a*sqrt(0.6))));
  return vec3(p, z);
}

vec3 doffset(float z) {
  const float eps = 0.05;
  return (offset(z + eps) - offset(z - eps))/(2.0*eps);
}

vec3 ddoffset(float z) {
  const float eps = 0.05;
  return (doffset(z + eps) - doffset(z - eps))/(2.0*eps);
}

vec3 skyColor(vec3 ro, vec3 rd) {
  return vec3(0.0);
}

// License: MIT, author: Pascal Gilcher, found: https://www.shadertoy.com/view/flSXRV
float atan_approx(float y, float x) {
  float cosatan2 = x / (abs(x) + abs(y));
  float t = PI_2 - cosatan2 * PI_2;
  return y < 0.0 ? -t : t;
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan_approx(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float modMirror1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize,size) - halfsize;
  p *= mod(c, 2.0)*2.0 - 1.0;
  return c;
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

float smoothKaleidoscope(inout vec2 p, float sm, float rep) {
  vec2 hp = p;

  vec2 hpp = toPolar(hp);
  float rn = modMirror1(hpp.y, TAU/rep);

  float sa = PI/rep - pabs(PI/rep - abs(hpp.y), sm);
  hpp.y = sign(hpp.y)*(sa);

  hp = toRect(hpp);

  p = hp;

  return rn;
}

vec3 palette( float t ) {
  return (1.0+cos(1.0*vec3(0.0, 1.0, 2.0)+TAU*t))*0.5;
}

// License: Unknown, author: kishimisu, found: https://www.shadertoy.com/view/mtyGWy
vec3 kishimisu(vec3 col, vec2 p, float tm, float n) {
  vec2 p0 = p;
  vec3 finalColor = vec3(0.0);

  vec2 p1 = p;
  for (float i = 0.0; i < 4.0; i++) {
    p1 = fract(p1 * 2.0+0.0125*tm) - 0.5;

    float d = length(p1) * exp(-length(p0));

    vec3 cc = palette(icolor + length(p0) + i*.4 + tm*.2);

    d = sin(d*8. + tm)/8.;
    d = abs(d);

    d -= 0.0025;
    d = max(d, 0.005);
    d = (0.0125 / d);
    d *= d;

    col += cc * d;
  }

  return 0.5*(col);
}

vec3 effect(vec2 p, float tm, float n) {
  vec3 col = vec3(0.0);
  vec2 kp = p;
  float kl = dot(kp, kp);
  float kpf = 0.;
  int nm3 = int(mod(n, 3.));
  if (nm3 == 0)      kpf = ipitch_6;
  else if (nm3 == 1) kpf = ipitch_7;
  else if (nm3 == 2) kpf = ipitch_8;
  float nn = 2.*floor(mix(3.0, 16.0, 0.5+0.5*sin(0.1*n)));
  float kn = smoothKaleidoscope(kp, 0.1, nn);
  kp += 0.5*sin(vec2(1.0, sqrt(0.5))*tm*(0.21+(.08*sin(kpf*.05))));
  col = kishimisu(col, kp, tm, n);
  col -= 0.75*vec3(0.0, 1.0, 2.0).zyx*dot(p, p);
  col = clamp(col, 0.0, 4.0);
  return col;
}

vec4 plane(vec3 ro, vec3 rd, vec3 pp, vec3 off, float aa, float n) {
  vec2 p = (pp-off*vec3(1.0, 1.0, 0.0)).xy;
  float l = length(p);
  p *= mix(0.5, 0.75, 0.5+0.5*sin(n*0.071));
  float tm = 0.*TIME+0.125*n;
  int nm3 = int(mod(n, 5.));
  if (nm3 == 0)      tm += ipitch_1 * .5;
  else if (nm3 == 1) tm += ipitch_2 * .5;
  else if (nm3 == 2) tm += ipitch_3 * .5;
  else if (nm3 == 3) tm += ipitch_4 * .5;
  else if (nm3 == 4) tm += ipitch_5 * .5;

  p *= ROT(-tm);
  float fade = smoothstep(0.1, 0.15, l);
  if (fade < 0.05) return vec4(0.0);
  vec4 col = vec4(0.0);

  col.xyz = effect(p, tm, n);
  float i = max(max(col.x, col.y), col.z)*0.75;
  col.w = (tanh_approx(0.5+l+max((i), 0.0))*fade);
  return col;
}

vec3 color(vec3 ww, vec3 uu, vec3 vv, vec3 ro, vec2 p) {
  float lp = length(p);
  vec2 np = p + 1.0/RESOLUTION.xy;
  const float rdd_per   = 10.0;
  float rdd =  (1.75+0.75*pow(lp,1.5)*tanh_approx(lp+0.9*PCOS(rdd_per*p.x)*PCOS(rdd_per*p.y)));
//  float rdd = 2.0;

  vec3 rd = normalize(p.x*uu + p.y*vv + rdd*ww);
  vec3 nrd = normalize(np.x*uu + np.y*vv + rdd*ww);

  float nz = floor(ro.z / planeDist);

  vec3 skyCol = skyColor(ro, rd);


  vec4 acol = vec4(0.0);
  const float cutOff = 1.;
  bool cutOut = false;

  float maxpd = 0.0;

  // Steps from nearest to furthest plane and accumulates the color
  for (int i = 1; i <= furthest; ++i) {
    float pz = planeDist*nz + planeDist*float(i);

    float pd = (pz - ro.z)/rd.z;

    if (pd > 0.0 && acol.w < cutOff) {
      vec3 pp = ro + rd*pd;
      maxpd = pd;
      vec3 npp = ro + nrd*pd;

      float aa = 3.0*length(pp - npp);

      vec3 off = offset(pp.z);

      vec4 pcol = plane(ro, rd, pp, off, aa, nz+float(i));

      float nz = pp.z-ro.z;
      float fadeIn = smoothstep(planeDist*float(furthest), planeDist*float(fadeFrom), nz);
      float fadeOut = smoothstep(0.0, planeDist*0.1, nz);
      pcol.w *= fadeOut*fadeIn;
      pcol = clamp(pcol, 0.0, 1.0);

      acol = alphaBlend(pcol, acol);
    } else {
      cutOut = true;
      acol.w = acol.w > cutOff ? 1.0 : acol.w;
      break;
    }

  }

  vec3 col = alphaBlend(skyCol, acol);
// To debug cutouts due to transparency
//  col += cutOut ? vec3(1.0, -1.0, 0.0) : vec3(0.0);
  return col;
}

vec3 effect(vec2 p, vec2 pp) {
  float tm  = planeDist*imoveFWD*10.;
  vec3 ro   = offset(tm);
  vec3 dro  = doffset(tm);
  vec3 ddro = ddoffset(tm);

  vec3 ww = normalize(dro);
  vec3 uu = normalize(cross(normalize(vec3(0.0,1.0,0.0)+ddro), ww));
  vec3 vv = cross(ww, uu);

  vec3 col = color(ww, uu, vv, ro, p);
  // col *= smoothstep(1.0, 10.0, TIME);
  col = clamp(col, 0.0, 1.0);
  col = sqrt(col);
  return col;
}

