// 2D pattern kata. Public domain.
// Brushing up my skills without looking at other people's code.

// Constraints:
// - fully deterministic (no hashing or noise)
// - rectangular or triangular grids
// - only two color channels
// - needs antialiasing (smoothsteps are fine)
// - WebGL 1 compatible, 60 fps on my old notebook
// - 2 kB

void mainImage(out vec4 o, vec2 x) {
    vec3 t = iTime * vec3(.1, .316, 1);

    vec3 rot = vec3(cos(t.x), sin(t.x), -sin(t.x));
    mat2 R = mat2(rot.xzyx),
        RT = mat2(rot.xyzx);  // slow rotation and its inverse

    float W = 15. / iResolution.y;  // antialiasing filter width

    vec2 u = (x+x-iResolution.xy)/iResolution.y,  // rect coordinates, y:-1..1
         r = R*5.*u + t.z,  // transformed rect coordinates (scale, rotation, shift)
         rr = round(r),
         h = .5*mat2(2,-1, 0,sqrt(3.)) * r;  // transformed hex coordinates: → x, ↖ y
    vec3 H = vec3(h, h.x+h.y),  // add third hex direction ↗ z=x+y
         rH = round(H);

    vec3 i;  // moving hex stripes, used for patterns 1 and 3
    {
      vec3 I = fract(H);
      float f = fract(t.x);
      i = smoothstep(f   , f   +W, I) - smoothstep(f+.5, f+.5+W, I)
        + smoothstep(f-1., f-1.+W, I) - smoothstep(f-.5, f-.5+W, I);
    }

    // Duotone color.
    vec2 c;

    // Borders between patterns.
    float Q = .5*iResolution.x/iResolution.y;  // half of x
    float P = floor(u.x/Q)+2. + 4.*(floor(u.y)+1.);  // pattern index 0..7
    if (any(lessThan(abs(vec3(u, abs(u.x)-Q)), vec3(.01)))) c = vec2(0);

    // Circles over squares.
    else if (P==0.) {
        // circles: additive outer radius, covering inner radius
        float cw = length(r - rr);
        vec2 C = smoothstep(cw, cw+W, vec2(.25, .35) + .125*sin(t.z));

        // squares: scaled by sqrt(2) - antialiasing filter needs to be wider
        float sw = .5 + .25*sin(t.y);
        vec2 s = fract(R*R*(5.*sqrt(2.))*u + t.z);  // rotate even more, scale
        vec2 S = smoothstep(sw, sw+W*sqrt(2.), s) + smoothstep(W*sqrt(2.), 0., s);

        c = 1. - vec2((1.-C.x)*min(S.x,S.y), C.y); }  // combine

    // Rhombi sliding around each other, sometimes forming Czech flag cubes.
    else if (P==1.) {
        vec3 d = i.xyz*(1.-i.yzx); c = d.yx + d.z; }

    // Rectangular grid with a gradient that's always vertical.
    else if (P==2.) {
        vec2 s = r - rr;  // local coords have 0 in the center
        vec2 aa = smoothstep(.45,.45+W, s) + smoothstep(-.5+W,-.5, s);
        float S = max(aa.x, aa.y);
        c = vec2(S, max(S, smoothstep(-.2,.7, (RT*s).y))); }  // unrotate the gradient

    // Sliding triangular stripes.
    else if (P==3.) {
        c = vec2(2.*i.xy + i.z)/3.; }

    // Right triangles and hexagons. Similar to the sliding rhombi, but rectangular.
    else if (P==4.) {
        vec3 m = fract(vec3(r, r.x+r.y) + t.x),
             WW = vec3(W,W,sqrt(2.)*W),  // diagonal antialias is wider
             j = smoothstep(vec3(.5),.5+WW, m) + smoothstep(WW,vec3(0), m);
        c = vec2(max(max(j.x,j.y),j.z), min(min(j.x,j.y),j.z)); }

    // Cute migrating blobs on a hexagonal grid.
    else if (P==5.) {
        vec3 i = H - rH;  // local coords have 0 in the center
        float q = mod(rH.x + rH.z, 3.),  // cell index: select from 3 colors
              w = .4 + .1*sin(t.z + (30. + 10.*sin(t.x))*u.y),  // warp radius
              s = 2.*(RT*i.xz).y + 1.;  // unrotate shading
        c = s * smoothstep(w+W, w, length(i)) * vec2(q<2.?1:0, q<1.?0:1); }  // antialias, color

    // Glowing triangular mesh.
    else if (P==6.) {
        float L = (length(fract(H) - .5) - .25) * 1.75;  // map to ~ 0..1
        c = pow(vec2(L), 3.+2.*cos(t.xy)); }  // modify glow exponent 1..5

    // Stairs and waves with fading echoes.
    else if (P==7.) {
        vec2 f = r - rr;
        float q = mod(rr.x + rr.y, 4.);  // rectangular cell index

        c = vec2(1,0);
        float l;  // get continuous stair- or wave field based on cell index, assign color
        if      (q<1.) { l = min(f.x,f.y)+.5; }
        else if (q<2.) { l = max(f.x,f.y)+.5; }
        else if (q<3.) { c=c.yx; l = length(f-.5); }
        else           { c=c.yx; l = 1. - length(f+.5); }

        vec4 p = .5 + .35*sin(vec4(t.z, floor(t.z)-vec3(0,1,2))),  // main moving line + static echo (floor)
             d = vec4(3, vec3(3,2,1)-fract(t.z))/3.,  // keep 3 echoes: get narrower and fade away
             w = .075*d,  // width
             v = d*d * smoothstep(p-w-W,p-w, vec4(l)) * smoothstep(p+w+W,p+w, vec4(l));  // intensity
        c *= max(max(max(v.x,v.y),v.z),v.w); }

    o = vec4(13,8,4,0);
    o = sqrt(o*c.x + o.zyxw*c.y + 1.) / 4.;  // orange + blue palette
}

