#define cloud(x) smoothstep(-.5, 1.5, noise(cuv*2. + iTime*.02 \
                + vec2(                                         \
                    noise(cuv, 2<<2, seed + uvec3(2)),           \
                    noise(cuv, 2<<2, seed + uvec3(3))             \
                )*.8,                                              \
                x, seed+uvec3(1))                                   \
    )                                               

#define eclipseCalc euv = cuv; euv.y -= 4.; euv.x -= iTime * .05 + 2.; euv.x = mod(euv.x + 4., 8.) - 4.; \
            eclipse = 1.-clamp( .3/(euv.x*euv.x + euv.y*euv.y) ,0.,1.);


