const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

mat2 rot(float t){
    return mat2(cos(t), sin(t), -sin(t), cos(t));
}

float noise(vec2 p, int d, uvec3 x){
    p *= 0.002;
    float sum;
    vec2 dir;
    vec3 h;
    for (int i = 1; i<d+1; i++){
        h = hash(x + uvec3(i, i*i, 2<<i)) * 2. - 1.;
        sum  += (texture(iChannel3, p * rot(atan(h.y, h.x)) * float(i)).x*2.-1.)/pow(float(i), 1./detail);
    }
    return sum;
}

float dirDeriv(vec2 p, int d, uvec3 x, vec2 dir, float e){
    return (noise(p,d,x) - noise(p - dir*e, d, x)); 
}

float dirDeriv(vec2 p, int d, uvec3 x, float f, vec2 dir, float e){
    return (f - noise(p - dir*e, d, x)); 
}

vec2 normal(vec2 p, int d, uvec3 x, float f, float e){
    return vec2(
        dirDeriv(p,d,x,f,vec2(1,0), e),
        dirDeriv(p,d,x,f,vec2(0,1), e)
    );
}

vec2 sphere( in vec3 ro, in vec3 rd, in vec3 ce, float ra )
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    vec3 qc = oc - b*rd;
    float h = ra*ra - dot( qc, qc );
    if( h<0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

float linePointDist(vec3 o, vec3 dir, vec3 p){
    vec3 a = cross(p-o, dir-o);
    vec3 b = normalize(cross(dir-o, a));
    return dot(b, p-o);
}


void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;

    uvec3 seed = uvec3(0,1,3);

    O = vec4(0);


    vec3 o = vec3(0);
    vec2 camuv = (cuv / 1.5) + vec2(0, .2);
    //vec3 dir = vec3(sin(camuv.x)*cos(camuv.y),cos(camuv.x)*cos(camuv.y), sin(camuv.y));
    //dir.zy *= rot(.5);
    vec3 dir = normalize(vec3(camuv.x, 2., camuv.y));
    vec2 t;
    t.x = sphere(o, dir, offset(radius), radius).x;
    t.y = sphere(o, dir, offset(radius), radius + atmosphere).x;
    float fresnel = t.x>0.?dot(-dir, normalize(o + dir*t.x - offset(radius))):1e10;

    float x = linePointDist(o, dir, offset(radius)) - radius;
    O += vec4(.2, .4, .8, 1.) * 1./sqrt(x*2.) * exp(-x*4.); //Help the people without oled screens feel a bit less bad... that includes me

    float eclipse;
    vec2 euv;
    o.x += iTime*.2;

    if (t.y > 0.){
        if (t.x > 0.){
            cuv = (o + dir * t.x).xy;

            eclipseCalc

            float f = noise(cuv*.2, 2<<5, seed);
            vec3 norm = normal(cuv*.2, 2<<5, seed, f, 1e-2 / t.x).xyy;
            norm.z = sqrt(1. - norm.x*norm.x - norm.y*norm.y);

            vec3 lightDir = normalize(vec3(sin(1.), cos(1.), 1.));
            float shading = dot(lightDir, norm);

            O = mix(vec4(.8,.8,.7,1.), vec4(.2, .3,.3,1.), f) * shading;

            if (f < 0.) O *= pow(vec4(.5, .6, .8, 1.), vec4(-f*5.));

            //Cloud Shadows
            O *= 1.-cloud(2<<3);
            // By simply doing less FBM we get blurred noise. A rare case of blur being cheaper!

            O *= eclipse;
        }

        cuv = (o + dir * t.y).xy;

        eclipseCalc

        // Cloud Layer
        O = mix(O, vec4(1), 
            cloud(2<<5) * eclipse
        );
    }

    //O = vec4(0);
    float f =  1./(sqrt(fresnel*10.)) * exp(-fresnel*5.);
    O /= (f + 1.);
    O += vec4(.2, .4, .8, 1.) * f * eclipse;


}

