float cylSDF(vec3 p, vec3 a, vec3 b, float r) {
  vec3 ba = b - a;
  vec3 pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}

float boxSDF(vec3 p, vec3 s) {
    p = abs(p) - s;
    return length(max(p, 0.0)) + min(0.0, max(p.x, max(p.y, p.z)));
}

#define SAMPLE(ID, DIST) if (DIST < dist) { dist = DIST; id = ID; }

#define ID_BODY 0
#define ID_CAP 1
#define ID_TABLE 2
#define ID_GROUND 3

float bodySDF(vec3 p, float h, float r) {
    float dm = abs(p.y-(h*0.5));
    float k = r;
    r = mix(r, k*2.25, slt(abs(p.y-(h*0.5)), h*0.3333, 0.6)*slt(p.y, h*0.9, 1.2));
    r = mix(r, k*2.25, slt(p.y, 0.9, 0.45));
    return cylSDF(p, vec3(0, 0, 0), vec3(0, h, 0), r);
}

float capSDF(vec3 p, float h, float r) {
    return cylSDF(p, vec3(0, h, 0), vec3(0, h+0.25, 0), r*1.1);
}

float bottleSDF(vec3 p, inout Data data) {
    int id = data.o.id;
    float dist = data.d;
    float h = 3.0;
    float r = 0.3;

    if (data.skip != ID_BODY) {
        float body = bodySDF(p, h, r);
        SAMPLE(ID_BODY, body);
    }
    float cap = capSDF(p, h, r);
    SAMPLE(ID_CAP, cap);
    data.o.id = id;
    return dist;
}

float tableSDF(vec3 p) {
    float w = 8.0;
    float depth = 4.0;
    float bh = 0.25;
    float h = 6.0;
    float r = 0.3333;

    float dist = FAR;
    float board = boxSDF(p, vec3(w, bh, depth));
    dist = min(dist, board);

    float inset = r*2.0;
    float leg = cylSDF(vec3(abs(p.x), p.y, abs(p.z)), vec3(w-inset, -h, depth-inset), vec3(w-inset, 0, depth-inset), r);

    dist = min(dist, leg);

    return dist;

}

float groundSDF(vec3 p) {
    if (p.y > 11.0) return p.y;
    vec3 n1 = tnoise(p.xz, 11.14925, 0.1, 0.1, 3);
    float h = n1.x*(10.*(1.0+(n1.y*n1.y)))*smoothstep(16.0, 30.0, length(p.xz));
    return p.y - h;
}

float sdf(vec3 p, inout Data data) {
    int id = data.o.id;
    float dist = data.d;

    float bottle = bottleSDF(p - vec3(0, 6.253, 0), data);
    id = data.o.id;
    dist = bottle;


    float table = tableSDF(p - vec3(0, 6, 0));

    SAMPLE(ID_TABLE, table);
    data.o.id = id;

    float ground = groundSDF(p);
    SAMPLE(ID_GROUND, ground);
    data.o.id = id;

    return dist;
}

bool march(vec3 ro, vec3 rd, inout Data data) {
    float d = 0.0;

    for (int i = ZERO; i < STEPS; i++) {
        vec3 p = ro+rd*d;
        float next = data.sig * sdf(p, data);
        if (abs(next) >= FAR) return false;
        if (abs(next) <= (NEAR*(1.0 + abs(d)))) break;
        d += next;
    }

    vec3 p = ro+rd*d;
    vec2 e = vec2(NEAR, 0);
    vec3 n = normalize(sdf(p, data) - vec3(
        sdf(p - e.xyy, data),
        sdf(p - e.yxy, data),
        sdf(p - e.yyx, data)
    ));

    data.p = p;
    data.n = n;
    data.d = d;

    return true;
}

float getShadow(vec3 ro, float NdotL, Light light) {
    Data data = NEW_DATA;
    float init = NEAR*9.0;
    float d = init;
    vec3 L = getLightDir(light, ro);
    vec3 rd = L;
    float ad = 0.0;

    for (int i = ZERO; i < 32; i++) {
        vec3 p = ro+rd*d;
        float next = sdf(p, data);
        //if (next >= FAR) return 1.0;
        if (next <= -NEAR) break;
        ad += next;
        d += clamp(next, 0.005, 2.0);
    }

    if (d <= init) return 1.0;
    float s = ad / (1.0 + ad);

    return clamp(s*s*s*s, AMBIENT, 1.0);
}


vec3 forEachLight(Light light, vec3 ro, vec3 rd, Data data, vec3 diffuse) {
  vec3 p = data.p;
  vec3 N = data.n;
  vec3 L = getLightDir(light, p);
  float NdotL = dot(N, L);
  vec3 att = getLightAtt(light, p, N);
  vec3 ref = reflect(N, L);
  float VdotR = max(0.0, dot(rd, ref));
  float spec = pow(VdotR, 24.0)*data.m.spec;
  float shadow = (data.sig < 0.0) ? 1.0 : (NdotL <= 0.015) ? max(AMBIENT, abs(NdotL)) : getShadow(data.p, NdotL, light);
  return ((diffuse + spec) * att) * shadow;
}

vec3 getAlbedoCap(inout Data data) {
    vec3 col = vec3(0.0);
    vec3 p = data.p;
    vec3 n = data.n;
    vec2 uv = mix(mix(p.xy, p.yz, (abs(dot(n, vec3(1, 0, 0))))), p.xz, (abs(dot(n, vec3(0, 1, 0)))));


    col = vec3(0.99, 0.1, 0.045);


    return col;
}


float chr(vec2 p, int c) {
    if (c == 32) return 0.0;
    p = (((p-vec2(0, 1))+vec2(float(int(1.0+float(c-97))),
    int(-6 - (c > 111 ? 1 : 0))))/16.);
    return texture(iChannel1, p).x;
}


#define TEXT(P, CHARS)\
{\
  int chars[] = int[] CHARS;\
  int len = chars.length();\
  vec2 id = floor((P)*(float(len/2)+1.));\
  vec2 lv = fract((P)*(float(len/2)+1.));\
  int idx = int(id.x);\
  text += (idx < 0 || idx >= len || abs(lv.x-0.9)<0.1 || abs(lv.y-0.9)<0.1) ? 0.0 : \
      chr(lv, chars[idx]);\
}


vec3 getAlbedoBody(inout Data data) {
    vec3 col = vec3(0.0);
    vec3 p = data.p;
    p.y -= 6.0;
    vec3 n = data.n;
    vec2 uv = mix(mix(p.xy, p.yz, (abs(dot(n, vec3(1, 0, 0))))), p.xz, (abs(dot(n, vec3(0, 1, 0)))));

    col = vec3(0.45, 0.1, 0.1);

    float pm = abs((p.y)-1.5);
    float label = slt(pm, 0.5, 0.02);

    col = mix(col, vec3(1.0), label);

    vec2 coord = p.xy;//mix(p.xy, p.zy, floor(abs(dot(n, vec3(1, 0, 0)))));

    coord.y += 0.1;
    coord.x += 0.5;
    coord *= 1.1;
    float text = 0.0;
    TEXT(coord, (99, 111, 108, 97))
    col = mix(col, vec3(0.0), text*label*float(p.y > 1.4 && p.y < 1.7));

    //col = mix(col, vec3(0.0), sc);

    data.m.ior = 1.490*max(0.0, 1.0-label);

    return col;
}

vec3 getAlbedoTable(inout Data data) {
    vec3 col = vec3(0.0);
    vec3 p = data.p;
    vec3 n = data.n;
    vec2 uv = mix(mix(p.xy, p.yz, (abs(dot(n, vec3(1, 0, 0))))), p.xz, (abs(dot(n, vec3(0, 1, 0)))));


    vec3 n1 = tnoise(uv, 0.31254, 2.0, 0.03, 2);
    vec3 n2 = tnoise(uv, 4.49583, 3.0, 0.03, 2);

    vec3 c1 = vec3(0.4, 0.2, 0.05);
    vec3 c2 = vec3(0.5, 0.21, 0.09);
    vec3 c3 = vec3(0.49, 0.29, 0.005);
    vec3 c4 = vec3(0.4, 0.2, 0.1);


    float px = 0.5+0.5*sin((2.0*n1.z+uv.x*6.28+2.2*(cos(uv.y*6.28+2.0*n1.x)))*(1.0+n2.y));
    float py = 0.5+0.5*cos((2.0*n1.x+uv.x*6.28+2.2*(sin(uv.y*6.28+2.0*n1.y)))*(1.0+n2.x));

    col = mix(col, c1, n1.x);
    col = mix(col, c2, n1.y);
    col = mix(col, c3, n1.z);
    col = mix(col, c4, n2.x);
    col = mix(col, col*col, px*n1.x*n2.y);

    data.m.spec = n2.x*max(0.0, 1.0-max(px, py))*n2.x*n2.x*0.5;

    return col;
}

vec3 getAlbedoGround(inout Data data) {
    vec3 col = vec3(0.0);
    vec3 p = data.p;
    vec3 n = data.n;

    vec3 c1 = vec3(0.1, 0.7, 0.05);
    vec3 c2 = vec3(0.25, 0.69, 0.09);
    vec3 c3 = vec3(0.09, 0.39, 0.02);
    vec3 c4 = vec3(0.25, 0.39, 0.05);

    vec3 n1 = tnoise(p.xz, 0.211155);
    vec3 n2 = tnoise(p.xz*0.25, 10.44233);
    vec3 n3 = tnoise(p.xz*0.25, 16.66343, 2.0, 0.05, 2);

    col = mix(c1, c2, n1.x);
    col = mix(col, c3, n1.y);
    col = mix(col, c4, smoothstep(0.4, 0.7, n3.x));
    col = mix(col, col*col, n2.x);

    return col;
}


vec3 getAlbedo(inout Data data) {
    switch (data.o.id) {
        case ID_CAP: return getAlbedoCap(data); break;
        case ID_BODY: return getAlbedoBody(data); break;
        case ID_TABLE: return getAlbedoTable(data); break;
        case ID_GROUND: return getAlbedoGround(data); break;
    }

    return vec3(0.48);
}

vec3 getSky(in vec3 rd) {
    float dotup = max(0.0, dot(rd, vec3(0, 1, 0)));
    vec3 col = pow(COLOR_SKY, vec3(1.0 + dotup));
    return col;
}

vec3 getColor(vec3 ro, vec3 rd, inout Data data) {
    vec3 col = vec3(0.0);

    Light light = Light(vec3(1, 3, -1), vec3(0.0), vec3(0.97, 0.89, 0.79), 2.0, LIGHT_AMBIENT);

    if (march(ro, rd, data)) {
        vec3 albedo = getAlbedo(data);
        vec3 diffuse = albedo / M_PI;
        col += forEachLight(light, ro, rd, data, diffuse);
    } else {
        col += getSky(rd);
    }

    return max(col, vec3(0.0));
}


void mainImage(out vec4 o, in vec2 fc)
{
    vec3 col = vec3(0.0);
    vec2 uv = (fc.xy - 0.5 * R.xy) / R.y;
    vec4 m = vec4((iMouse.xy - 0.5 * R.xy) / R.y, iMouse.zw);

    vec3 ro = vec3(0, 1.9, -5);
    vec3 rd = normalize(vec3(uv, 1.0));

    if (m.z > 0.001) {
        ro.yz *= rot(m.y*TAU);
        ro.xz *= rot(m.x*TAU);
        rd.yz *= rot(m.y*TAU);
        rd.xz *= rot(m.x*TAU);
    } else {
        float ten = sgt(iTime, 8.0, 0.5)*slt(iTime, 12.0, 0.5);
        ro.z -= mix((0.5+0.5*sin(iTime))*3.0, 0.0, ten);
        float ang = mix(iTime, 0.0, ten);
        //ro.yz *= rot(ang);
        ro.xz *= rot(ang);
       // rd.yz *= rot(ang);
        rd.xz *= rot(ang);
    }

    ro.y += 6.0;

    Data data = NEW_DATA;
    col += getColor(ro, rd, data);

    if (data.m.ior > 0.01) {
        vec3 n = data.n;
        vec3 p = data.p;
        float ior = data.m.ior;

        vec3 enterDir = refract(rd, n, 1.0/ior);
        Data enterData = NEW_DATA;
        enterData.sig = -data.sig;


         vec3 enterP = p+(n*enterData.sig*NEAR*10.);
        vec3 enterCol = getColor(enterP, enterDir, enterData);


        Data exitData = NEW_DATA;
        exitData.sig = -enterData.sig;
        exitData.skip = ID_BODY;
        vec3 exitDir = refract(enterDir, enterData.n, ior);


        if (length(exitDir) < 0.000001) {
          exitDir = reflect(enterDir, enterData.n);
          exitData.sig *= -1.0;
        }


        vec3 exitP = enterData.p+(enterData.n*NEAR*enterData.sig);
        vec3 exitCol =  getColor(exitP, exitDir, exitData);
        col =  exitCol * enterCol* M_PI;
    }

    col = aces(col);
    col = pow(col, vec3(1.0/2.2));

    o = vec4(col, 1.0);
}

