mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c,-s,s,c);
}
//@iq
float sdBox( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec2 boxes(vec3 p) {
    vec2 h,t = vec2(1000,0);
    vec3 q = p;
    q.xy *= rot(ipitch_6);
    q.zx *= rot(-ipitch_8);
    h = vec2(sdBox(abs(q)-vec3(4,0,0), vec3(1,4,1))-.1, 1);
    t = h.x<t.x ? h : t;

    q = p;
    q.xy *= rot(ipitch_7);
    q.zx *= rot(-ipitch_9);
    h = vec2(sdBox(abs(q)-vec3(0,4,2), vec3(4,1,1))-.1, 1);
    t = h.x<t.x ? h : t;
    return t;
}

vec2 map(vec3 p) {
    // kaleidoscope effect adapted from @NuSan
    for (float i=0.; i<4.; i++) {
        p = abs(p);
        p.xy *= rot(sin(ipitch_1*.5 + i*2.));
        p -= i*mix(1.,1.5,sin(ipitch_2)*.5+.5);
    }
    p.xy *= rot(ipitch_3*.5);
    p.yz *= rot(ipitch_4*.5);
    vec2 t = min(boxes(p), boxes(p*2.)/2.);
    return t;
}

vec3 normal(vec3 p) {
    vec2 e = vec2(.001,0);
    return normalize(map(p).x - vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

void cam(inout vec3 p) {
    p.xz *= rot(iTime*.2);
}

