    vec3 rd = normalize(vec3(uv, 1));

    //cam(ro);cam(rd);

    float t = 0.;
    for (float i=0.; i<100.; i++) {
        vec3 p = ro + rd*t;
        vec2 h = map(p);
        float d = h.x;
        float m = h.y;
        t += d;
        if (t>FAR) {
            break;
        }
        if (d < .001) {
            vec3 sn = normal(p);
            col = .5+.5*cos((sn*.3+.15)*PI+icolor+vec3(0,4,8).yxz + dot(sn,vec3(1))*PI );
            break;
        }
        float a = atan(uv.y,uv.x);
        col += (1./d *.02)*(.5*.5+cos(imoveFWD+a+vec3(0,2,4)));
    }

#ifdef ANIMATION_FRACTAL
    col = pow(col, vec3(1.2, .9, .9) * 2.);
#endif
    oColor.xyz = pow(col,vec3(.4545));
}

