    float n_sd_star = f_n_sd_star(uv, .5, 5, 3.);
    float n_sd_heart = f_n_sd_heart(uv+vec2(0., 0.5));
    float n_t = sin(iTime)*.5+.5;//1.0 max, 0.0 min, 1.0 ->nur kreis, 0.0 ->nur stern
    float n_interpoliert = n_t*n_sd_circ + (1.-n_t)*n_sd_star;
    fragColor = vec4(smoothstep(0.05, 0.04, n_interpoliert));
}

precision mediump float;

#define PI 3.14159265359
#define TAU 6.28318530718

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts

vec4 permute(vec4 x) {
    return mod(((x * 34.0) + 1.0) * x, 289.0);
}

vec4 taylorInvSqrt(vec4 r) {
    return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v){ 
    const vec2 C = vec2(1.0 / 6.0, 1.0 / 3.0);
    const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);

    // First corner
    vec3 i  = floor(v + dot(v, C.yyy));
    vec3 x0 =   v - i + dot(i, C.xxx);

    // Other corners
    vec3 g = step(x0.yzx, x0.xyz);
    vec3 l = 1.0 - g;
    vec3 i1 = min( g.xyz, l.zxy);
    vec3 i2 = max( g.xyz, l.zxy);

    //  x0 = x0 - 0. + 0.0 * C 
    vec3 x1 = x0 - i1 + 1.0 * C.xxx;
    vec3 x2 = x0 - i2 + 2.0 * C.xxx;
    vec3 x3 = x0 - 1. + 3.0 * C.xxx;

    // Permutations
    i = mod(i, 289.0); 
    vec4 p = permute(permute(permute( 
        i.z + vec4(0.0, i1.z, i2.z, 1.0)) +
        i.y + vec4(0.0, i1.y, i2.y, 1.0)) +
        i.x + vec4(0.0, i1.x, i2.x, 1.0)
    );

    // Gradients
    // ( N*N points uniformly over a square, mapped onto an octahedron.)
    float n_ = 1.0/7.0; // N=7
    vec3  ns = n_ * D.wyz - D.xzx;

    vec4 j = p - 49.0 * floor(p * ns.z *ns.z); // mod(p,N*N)

    vec4 x_ = floor(j * ns.z);
    vec4 y_ = floor(j - 7.0 * x_ ); // mod(j,N)

    vec4 x = x_ *ns.x + ns.yyyy;
    vec4 y = y_ *ns.x + ns.yyyy;
    vec4 h = 1.0 - abs(x) - abs(y);

    vec4 b0 = vec4( x.xy, y.xy );
    vec4 b1 = vec4( x.zw, y.zw );

    vec4 s0 = floor(b0)*2.0 + 1.0;
    vec4 s1 = floor(b1)*2.0 + 1.0;
    vec4 sh = -step(h, vec4(0.0));

    vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
    vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

    vec3 p0 = vec3(a0.xy,h.x);
    vec3 p1 = vec3(a0.zw,h.y);
    vec3 p2 = vec3(a1.xy,h.z);
    vec3 p3 = vec3(a1.zw,h.w);

    //Normalise gradients
    vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
    p0 *= norm.x;
    p1 *= norm.y;
    p2 *= norm.z;
    p3 *= norm.w;

    // Mix final noise value
    vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
    m = m * m;
    return 42.0 * dot(m*m, vec4(dot(p0,x0), dot(p1,x1), dot(p2,x2), dot(p3,x3)));
}

// Bitmap to ASCII (not really) fragment shader by movAX13h, September 2013
// This is the original shader that is now used in PixiJs, FL Studio and various other products.

// Here's a little tool for new characters: thrill-project.com/archiv/coding/bitmap/

float character(int n, vec2 p) {
	p = floor(p*vec2(-4.0, 4.0) + 2.5);

    if (clamp(p.x, 0.0, 4.0) == p.x) {
        if (clamp(p.y, 0.0, 4.0) == p.y) {
        	int a = int(round(p.x) + 5.0 * round(p.y));

			if (((n >> a) & 1) == 1) return 1.0;
		}	
    }
	return 0.0;
}

vec3 image(vec2 coord){
    vec2 aspect;

    if(iResolution.x > iResolution.y) {
        aspect = vec2(iResolution.x / iResolution.y, 1.0);
    } else { 
        aspect = vec2(iResolution.y / iResolution.x, 1.0);
    }

    vec2 uv0 = coord.xy / iResolution.xy * aspect;
    vec2 muv = iMouse.xy / iResolution.xy * aspect;

    //

    float noiseStrength = 0.16;
    float noiseScale = 0.5;

    float speed = 0.1;
    float noiseTime = iTime * speed;

    float noise = snoise(vec3(uv0 * noiseScale - noiseTime, noiseTime));

    uv0.x += noiseStrength * sin(noise * TAU);
    uv0.y += noiseStrength * cos(noise * TAU);

    muv.x += noiseStrength * sin(noise * TAU);
    muv.y += noiseStrength * cos(noise * TAU);

    //

    float strength =  0.6;

    vec2 diff = uv0 - muv;
    float distance = length(diff);

    float scale = -smoothstep(
        0.0,
        1.0,
        max(
            0.,
            1. - abs(distance)
        )
    );

    vec2 fishUv = muv + normalize(diff) * scale;

    uv0 = uv0 * (1.0 - strength) + fishUv *  strength;

    //

    // Fork of "Creation by Silexars" by Danguafer. https://shadertoy.com/view/XsXXDn
    // http://www.pouet.net/prod.php?which=57245
    // If you intend to reuse this shader, please add credits to 'Danilo Guanabara'

    vec3 c;
    float l, z = iTime;

	for(int i = 0; i < 3; i++) {
		vec2 uv, p = uv0;

		p -= aspect/2.0;
		z += .03;

		l = length(p);
		uv += p / l * (sin(z)+1.)*abs(sin(l*9.-z-z));
		c[i] = .05/length(mod(uv,1.)-.5);
	}

	return vec3(c/l);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 pix = fragCoord.xy;
	vec3 col = image(floor(pix/16.0)*16.0);	

	float gray = 0.3 * col.r + 0.59 * col.g + 0.11 * col.b;

    // limited character set 5x5px

	int n = 0;                            //   0px    
	if (gray > 4.0 / 25.0) n = 4;         // . 1px (bg tuned to 4/25)
	if (gray > 5.0 / 25.0) n = 8521864;   // > 5px
	if (gray > 8.0 / 25.0) n = 17318430;  // l 8px
	if (gray > 9.0 / 25.0) n = 14815374;  // i 9px
	if (gray > 12.0 / 25.0) n = 15255086; // o 12px
    if (gray > 13.0 / 25.0) n = 18667121; // n 13px
	if (gray > 15.0 / 25.0) n = 33059359; // e 15px

	vec2 p = mod(pix/8.0, 2.0) - vec2(1.0);

    col = gray * vec3(character(n, p));

	fragColor = vec4(col, 1.0);
    // fragColor = vec4(image(fragCoord.xy), 1.0);
}

