// Use a more precise definition of PI for mathematical operations
const float PI = 3.141592653589793;
const float FREQ_RANGE = 64.0; // or another appropriate value

#define RADIUS 0.6
#define BRIGHTNESS 0.2
#define SPEED 0.5


// Convert HSV to RGB: Ensuring const correctness and vec3 for p
vec3 hsv2rgb(const vec3 c) {
    const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


vec3 gradientColor(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.0, 0.33, 0.67); // these control the range of colors
    return a + b * cos(2.0 * PI * (c * t + d));
}

// Improved luminance calculation with const quality for weights
float luminance(const vec3 color) {
  const vec3 lumWeight = vec3(0.299, 0.587, 0.114);
  return dot(color, lumWeight);
}


// Enhanced frequency retrieval with smooth interpolation between index frequencies
float getFrequency(float x) {
	float baseIndex = floor(x * FREQ_RANGE) / FREQ_RANGE;
	float sum = 0.0;
	float count = 0.0;


	// Smoothing by averaging over a range of indices
	for(float i = -2.0; i <= 2.0; i += 1.0){
		sum += texture(iChannel0, vec2(baseIndex + i / FREQ_RANGE, 0.25)).x;
		count += 1.0;
	}


	return (sum / count) + 0.06;
}


// Blending frequencies for smoother visual transitions
float blendFrequencies(const float x) {
    float blended = smoothstep(0.0, 1.0, fract(x * FREQ_RANGE));


    float freq1 = getFrequency(floor(x * FREQ_RANGE) / FREQ_RANGE);
    float freq2 = getFrequency(floor(x * FREQ_RANGE + 1.0) / FREQ_RANGE);


    return mix(freq1, freq2, blended);
}


// Rendering the halo effect
vec3 renderHalo(const vec2 fragment, const float radius) {
    float dist = length(fragment);
    float ring = 1.0 / abs(dist - radius);
    float brightness = dist < radius ? BRIGHTNESS * 0.3 : BRIGHTNESS;
    vec3 col = vec3(0.0);


    float angle = atan(fragment.x, fragment.y);
    vec3 hsvColor = vec3((angle + iTime * 0.25) / (PI * 2.0), 1.0, 1.0);
    col += hsv2rgb(hsvColor) * ring * brightness;

    float frequency = max(blendFrequencies(abs(angle / PI)) - 0.02, 0.0);
    col *= frequency;
    col *= smoothstep(radius * 0.5, radius, dist);

    float t = iTime * 0.1 + (angle + 0.5) / (PI * 2.0); // Adjust time influence
    col += gradientColor(t) * ring * (brightness * 0.2); // Use the gradient function

    return col;
}

// Generating the line visual based on frequency
vec3 renderLine(const vec2 fragment, const float radius, const float x) {
    vec3 col = hsv2rgb(vec3(x * 0.23 + iTime * 0.12, 1.0, 1.0));
    float freq = abs(fragment.x * 0.5);
    col *= (1.0 / abs(fragment.y)) * BRIGHTNESS * getFrequency(freq);  
    col *= smoothstep(radius, radius * 1.8, abs(fragment.x));
    return col;
}


// Main image function to render the visuals
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 fragPos = (fragCoord / iResolution.xy - 0.5) * 2.0;
    fragPos.x *= iResolution.x / iResolution.y;


    vec3 color = vec3(0.0134, 0.052, 0.1);
    color += renderHalo(fragPos, RADIUS);


    // Rotating the position for dynamic effects
    float c = cos(iTime * SPEED);
    float s = sin(iTime * SPEED);
    vec2 rot = mat2(c, s, -s, c) * fragPos;
    color += renderLine(rot, RADIUS, rot.x);


    color += max(luminance(color) - 1.0, 0.0);


    fragColor = vec4(color, 1.0);
}

//#define GRID
#define SHADE

// Max fractal iterations
#define MAXITERS 120
// Anti-aliasing (will compute (AA*2+1)^2 samples, so 1 for 0, 9 for 1, 25 for 2...)
#define AA 1

// AA breaks down near precision-limit levels of zoom
// Also, if the AA looks like barf, you're probably looking at a very noisy boundary.

vec4 f(vec4 p){
    return vec4(p.x*p.x-p.y*p.y+p.z,abs(2.0*p.x*p.y)+p.w,p.zw);
    // Remove the "abs" (keep the parentheses) for a mandelbrot fractal!
}

vec2 UV(vec2 px,vec3 iResolution,vec4 cam){
    return (px-0.5*iResolution.xy)/iResolution.y*cam.z-cam.xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragCoord.y = iResolution.y-fragCoord.y; // Mirror vertically except im lazy (and who's gonna stop me)

    vec4 cam = texelFetch(iChannel0,TEXEL_CAM,0);

    float pxSize = 1.0/iResolution.y*cam.z;
    float gridSize = pow(2.0,round(log2(0.25*cam.z)));

    vec2 uv = UV(fragCoord,iResolution,cam);

    float val = 0.0;
    float iters = 0.0;
    for (int x=0-AA;x<1+AA;x++){
        for (int y=0-AA;y<1+AA;y++){
            vec4 p = (uv+(0.5/float(AA))*pxSize*vec2(x,y)).xyxy; // I chose 0.333 arbitrarily, as I needed a value large enough to produce nice antialiasing but small enough to not just blur everything.
            for (int i=0;i<MAXITERS;i++){
                p = f(p);
                if (isinf(p.x) || isinf(p.y)){
                    iters+=float(i);
                    break;
                }
            }
            if (length(p.xy)<4.0){
                val+=1.0;
            }
        }
    }

    float pxCount = float((2*AA+1)*(2*AA+1));

    val /= pxCount; // Because we do 9 antialiasing samples
    iters /= pxCount;

    vec3 col = vec3(val);
    #ifdef SHADE
    col = mix(2.0*vec3(0,0,iters/float(MAXITERS)),col,val);
    #endif

    #ifdef GRID
    vec2 offset = UV(fragCoord-vec2(1.0),iResolution,cam);
    if (offset.x!=uv.x && offset.y!=uv.y){ // Check if we're hitting precision limits
        float halfCell = 0.5*gridSize;
        if (mod(uv.x-halfCell,gridSize)>=halfCell && mod(offset.x-halfCell,gridSize)<halfCell){
            col+=vec3(0.0,0.5,0.0);
        }
        if (mod(uv.y-halfCell,gridSize)>=halfCell && mod(offset.y-halfCell,gridSize)<halfCell){
            col+=vec3(0.0,0.5,0.0);
        }
    }
    #endif

    fragColor = vec4(col,1.0);
}

#define  KEY_LEFT 37
#define    KEY_UP 38
#define KEY_RIGHT 39
#define  KEY_DOWN 40

#define     KEY_A 65
#define     KEY_D 68
#define     KEY_S 83
#define     KEY_W 87

// Zooming is framerate dependant. I can't figure out how to uncouple zooming from the framerate effectively.
// One would think simply multiplying ZOOMFAC by the timestep would work, but what if the timestep is greater than 1? I know such a situation is absurd, but it proves that this method is flawed. I'm not sure how to fix this.
#define ZOOMFAC 0.02

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    ivec2 iCoord = ivec2(fragCoord);
    vec4 data = texelFetch(iChannel0,iCoord,0);

    if (iCoord == TEXEL_CAM){

        // (X position, Y position, Zoom, Unused)

        float moveScale = iTimeDelta*data.z;
        if (texelFetch(iChannel1,ivec2(KEY_W,0),0).r>0.5){
            data.y+=moveScale;
        }
        if (texelFetch(iChannel1,ivec2(KEY_S,0),0).r>0.5){
            data.y-=moveScale;
        }
        if (texelFetch(iChannel1,ivec2(KEY_A,0),0).r>0.5){
            data.x+=moveScale;
        }
        if (texelFetch(iChannel1,ivec2(KEY_D,0),0).r>0.5){
            data.x-=moveScale;
        }

        if (texelFetch(iChannel1,ivec2(KEY_UP,0),0).r>0.5){
            data.z*=(1.0-ZOOMFAC);
        }
        if (texelFetch(iChannel1,ivec2(KEY_DOWN,0),0).r>0.5){
            data.z/=(1.0-ZOOMFAC);
        }

        if (texelFetch(iChannel1,ivec2(KEY_LEFT,0),0).r>0.5){
            data.z*=(1.0-3.0*ZOOMFAC);
        }
        if (texelFetch(iChannel1,ivec2(KEY_RIGHT,0),0).r>0.5){
            data.z/=(1.0-3.0*ZOOMFAC);
        }

        if (data.z<=0.0 || iFrame==0){
            data = vec4(0.5,0.5,2.5,0.0);
        }

        data.x = clamp(data.x,-6.0,7.0);
        data.y = clamp(data.y,-3.0,4.0);

        data.z = min(data.z,9.0);
    }

    fragColor = data;
}

const float render_res = 65.;

vec3 render(vec2 coord)
{
    vec2 uv = 4. * screen_to_uv_fit(coord, vec2(render_res));
    uv.x += .22 * iTime;
    return vec3(cos(uv.x) * sin(uv.y) < sin(TAU * .03 * iTime));
}

vec3 discrete_render(ivec2 icoord)
{
    vec2 coord = vec2(icoord) + .5;
    vec3 col = vec3(0);

    col.r += render(coord + vec2(-.333, -.333)).r;
    col.r += render(coord + vec2(-.333, 0)).r;
    col.r += render(coord + vec2(-.333, .333)).r;

    col.g += render(coord + vec2(0, -.333)).g;
    col.g += render(coord + vec2(0, 0)).g;
    col.g += render(coord + vec2(0, .333)).g;

    col.b += render(coord + vec2(.333, -.333)).b;
    col.b += render(coord + vec2(.333, 0)).b;
    col.b += render(coord + vec2(.333, .333)).b;

    return col * .333;
}

vec3 view_transform(vec3 col)
{
    // avoid any negative values before using power functions
    col = max(col, 0.);

    // OETF (Linear BT.709 I-D65 to sRGB 2.2)
    col = pow(col, vec3(1. / 2.2));
    col = clamp(col, 0., 1.);

    return col;
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    vec2 uv = screen_to_uv_fit(frag_coord, iResolution.xy);
    vec3 col = discrete_render(ivec2(floor(uv * render_res * .5)));

    // view transform
    col = view_transform(col);

    // output
    frag_col = vec4(col, 1.);
}

