uint prng_state[2];

uint prng_rot(uint x, int k)
{
    return (x << k) | (x >> (32 - k));
}

// random uint from 0 to 2^32-1
uint randomui()
{
    uint s0 = prng_state[0];
    uint s1 = prng_state[1];
    uint result = prng_rot(s0 * 2654435771u, 5) * 5u;
    s1 ^= s0;
    prng_state[0] = prng_rot(s0, 26) ^ s1 ^ (s1 << 9);
    prng_state[1] = prng_rot(s1, 13);
    return result;
}

// random int from 0 to 2^31-1
int randomi()
{
    return int(randomui() % 0x7FFFFFFFu);
}

// random float from 0 to 1
float random()
{
    return float(randomui()) / float(0xffffffffu);
}

// generate two normally distributed random numbers using the
// Box-Muller transform
// https://www.baeldung.com/cs/uniform-to-normal-distribution
vec2 random_gauss()
{
    float u1 = random();
    float u2 = random() * TAU;
    float temp = sqrt(-2. * log(u1));
    return temp * vec2(cos(u2), sin(u2));

    // unoptimized version
    //float u1 = random();
    //float u2 = random();
    //return vec2(cos(TAU * u2), sin(TAU * u2)) * sqrt(-2. * log(u1));
}

vec2 random_on_circle()
{
    vec2 v = vec2(1);
    float lensqr;

    for (int i = 0; i < 20; i++)
    {
        v = vec2(2. * random() - 1., 2. * random() - 1.);
        lensqr = dot(v, v);
        if (lensqr == 0.)
            i--;
        else if (lensqr <= 1.)
            break;
    }
    return v / sqrt(lensqr);
}

vec3 random_on_sphere()
{
    vec3 v = vec3(1);
    float lensqr;
    for (int i = 0; i < 20; i++)
    {
        v = vec3(2. * random() - 1., 2. * random() - 1., 2. * random() - 1.);
        lensqr = dot(v, v);
        if (lensqr == 0.)
            i--;
        else if (lensqr <= 1.)
            break;
    }
    return v / sqrt(lensqr);
}

vec2 random_in_circle()
{
    vec2 v = vec2(1);
    for (int i = 0; i < 20; i++)
    {
        v = vec2(2. * random() - 1., 2. * random() - 1.);
        if (dot(v, v) <= 1.)
            return v;
    }
    return v;
}

vec3 random_in_sphere()
{
    vec3 v = vec3(1);
    for (int i = 0; i < 20; i++)
    {
        v = vec3(2. * random() - 1., 2. * random() - 1., 2. * random() - 1.);
        if (dot(v, v) <= 1.)
            return v;
    }
    return v;
}

vec3 random_on_hemisphere(vec3 normal)
{
    vec3 v = random_on_sphere();
    return v * sign(dot(v, normal));
}

vec3 random_in_hemisphere(vec3 normal)
{
    vec3 v = random_in_sphere();
    return v * sign(dot(v, normal));
}

// initialize with uvec2
void prng_init(uvec2 seed)
{
    seed += uvec2(1317, 944573125);
    seed *= 464973573u;
    prng_state[0] = seed.x;
    prng_state[1] = seed.y;
    randomi();
}

// initialize with uint
void prng_init(uint seed)
{
    prng_init(uvec2(seed, 1));
}

// initialize with vec3
void prng_init(vec3 seed)
{
    seed += 3.49276101561702;
    seed.xy *= (seed.z + 10.258);
    prng_state[0] = floatBitsToUint(seed.x);
    prng_state[1] = floatBitsToUint(seed.y);
    randomui();
}

// initialize with vec2
void prng_init(vec2 seed)
{
    prng_init(vec3(seed, 1));
}

// initialize with float
void prng_init(float seed)
{
    prng_init(vec3(seed, 1, 1));
}



/*______________ halton sequence ______________*/

// * idx starts at 1
float halton(int base, int idx)
{
    float result = 0.;
    float digit_weight = 1.;
    while (idx > 0)
    {
        digit_weight /= float(base);
        result += float(idx % base) * digit_weight;
        idx /= base;
    }
    return result;
}

// * idx starts at 1
vec2 halton_2d(int idx)
{
    return vec2(halton(2, idx), halton(3, idx));
}

// * idx starts at 1
vec3 halton_3d(int idx)
{
    return vec3(halton(2, idx), halton(3, idx), halton(5, idx));
}

// * idx starts at 1
vec4 halton_4d(int idx)
{
    return vec4(
        halton(2, idx),
        halton(3, idx),
        halton(5, idx),
        halton(7, idx)
    );
}

// Fork of "Hemispherical AO" by djancool. https://shadertoy.com/view/MftSzn
// 2024-04-19 10:51:41

//#define randomDirPerPixel

vec3 hash3( uvec3 x )
{
    const uint k = 1103515245U;

    x = ((x >> 8U) ^ x.yzx) * k;
    x = ((x >> 8U) ^ x.yzx) * k;
    x = ((x >> 8U) ^ x.yzx) * k;

    return vec3(x)*(1.0/float(0xffffffffU));
}


vec3 rndHemiDir(vec3 normal, uvec3 index)
{
    vec3 rnd = 2.0 * (0.5 - hash3(index));
    rnd = tan(rnd);
    rnd = normalize(rnd);

    // Uniform distribution
    return rnd * sign(dot(normal, rnd));

    // Cosine weighted distribution // We will have les samples on angles, wich makes them darker, wich simulates lambertian
    return normalize(normal + rnd);
}

vec3 uvToPos(vec2 uv)
{
    vec3 pos = vec3(0.0);
    pos.xy = uv;
    pos.z = texture(iChannel0, uv).r / 1.0;

    return pos;
}

vec3 getNormal(vec2 uv)
{
    float pixelSize = 5.0 / iChannelResolution[0].r; // use higher number than 1/resolution to smooth out normal to reduce artifacts

    vec3 pos_0 = uvToPos(uv);
    vec3 pos_x = uvToPos(uv + vec2(pixelSize, 0.0));
    vec3 pos_y = uvToPos(uv + vec2(0.0, pixelSize));

    vec3 normal = normalize(cross(pos_0 - pos_x, pos_0 - pos_y));
    return normal;
}

float getAO(vec2 uv, vec2 fragCoord)
{
    const float sampleDist = 0.3;
    const int sampleCount = 100;

    vec3 position = uvToPos(uv);
    vec3 normal = getNormal(uv);

    float AO = 0.0;

    for(int i = 0; i < sampleCount; i++)
    {
        #ifdef randomDirPerPixel
            uvec3 index = uvec3(uvec3(i, i + 50, i + 100) + uvec3(fragCoord, 0.0));
        #else
            uvec3 index = uvec3(uvec3(i, i + 50, i + 100));
        #endif
        vec3 sampleDir = rndHemiDir(normal, index);

        bool isOccluded = false;

        for(int j = 1; j < 5; j++)
        {
            vec3 samplePos = position + sampleDir / 5.0 * float(j) * sampleDist;
            float sampleHeight = uvToPos(samplePos.xy).z;

            if(samplePos.z < sampleHeight) 
           {
               AO += 1.0;
               break;
            }
        }

    }

    return 1.0 - AO / float(sampleCount);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y * 0.75;

    float AO = getAO(uv, fragCoord);

    fragColor = pow(vec4(AO), vec4(1.0 / 2.2));
}

