struct Cell
{
    vec4 color;
    float movementSpeedMultiplier;
};

const Cell cells[] =
    Cell[]
    (
        Cell(vec4(0.0, 0.0, 1.5, 1.0), 0.3),
        Cell(vec4(0.0, 1.5, 0.0, 1.0), 0.4),
