    );

const float startingHue = 1.0 - 0.0625;

const float minimalGridAxisCellCount = 5.0;

const float cellGlowSize = 0.0625;

const float cellGlowBoost = 1.5;

const float cellHueOffset = 0.05;

const vec3 topSkyColor = vec3(0.0, 0.0, 0.75);

const vec3 bottomSkyColor = vec3(0.0, 0.0, 0.25);

const float loopTimeInSeconds = 10.0;

const float hueShiftTimeInSeconds = 100.0;

const float e = 0.577215664901532860606512090082402431042159335;

#if defined SCREENSHOT_MODE
const float screenshotTime = e * 2.0;
#endif

vec4 AlphaBlendColors(const vec4 baseColor, const vec4 appendColor)
{
    vec3 color = mix(baseColor.rgb, appendColor.rgb, appendColor.a);
    return vec4(color.x, color.y, color.z, baseColor.a + ((1.0 - baseColor.a) * appendColor.a));
}

vec3 GetHSVFromRGB(vec3 rgb)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(rgb.bg, K.wz), vec4(rgb.gb, K.xy), step(rgb.b, rgb.g));
    vec4 q = mix(vec4(p.xyw, rgb.r), vec4(rgb.r, p.yzx), step(p.x, rgb.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 GetRGBFromHSV(vec3 hsv)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(hsv.xxx + K.xyz) * 6.0 - K.www);
    return hsv.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), hsv.y);
}

vec3 GetNormalizedRGB(vec3 rgb)
{
    vec3 hsv = GetHSVFromRGB(rgb);
    return (hsv.z > 1.0) ? GetRGBFromHSV(vec3(hsv.x, hsv.y / (hsv.z * hsv.z), 1.0)) : rgb;
}

vec4 DrawCell(const vec2 uv, vec4 emissiveColor, float glowSize, float glowBoost)
{
    vec4 ret;
    float left = uv.y * 0.25;
    float right = 0.75 + (uv.y * 0.25);
    if
    (
        (uv.x >= (left - glowSize)) &&
        (uv.x <= (right + glowSize)) &&
        (uv.y >= -glowSize) &&
        (uv.y <= 1.0)
    )
    {
        float intensity =
            (
                (uv.x < left) ?
                    (1.0 - ((left - uv.x) / glowSize)) :
                    (
                        (uv.x > right) ?
                            (1.0 - ((uv.x - right) / glowSize)) :
                            1.0
                    )
            ) *
            (
                (uv.y < 0.0) ?
                    (1.0 - (-uv.y / glowSize)) :
                    (
                        (uv.x < left) ?
                            1.0 :
                            (
                                (uv.x > right) ?
                                    1.0 :
                                    (uv.y / glowBoost)
                            )
                    )
            ) * glowBoost;
        vec3 color = GetNormalizedRGB(emissiveColor.rgb * emissiveColor.a * intensity);
        ret.rgba = vec4(color.r, color.g, color.b, intensity * emissiveColor.a * (1.0 - uv.y));
    }
    return ret;
}

void mainImage(out vec4 fragmentColor, in vec2 fragmentCoordinates)
{
#if defined SCREENSHOT_MODE
    const float hue = startingHue;
#else
    float hue = startingHue + (iTime / hueShiftTimeInSeconds);
#endif
    vec2 uv = (fragmentCoordinates.xy - (iResolution.xy * 0.5)) / ((iResolution.x > iResolution.y) ? iResolution.y : iResolution.x);
    vec3 sky_hsv = GetHSVFromRGB(mix(bottomSkyColor, topSkyColor, uv.y));
    vec4 sky_color = vec4(GetRGBFromHSV(vec3(sky_hsv.x + hue, sky_hsv.y, sky_hsv.z)), 1.0);
    vec4 color = vec4(clamp(sky_color.r, 0.0, 1.0), clamp(sky_color.g, 0.0, 1.0), clamp(sky_color.b, 0.0, 1.0), 1.0);
#if defined SCREENSHOT_MODE
    float y_movement = mod(screenshotTime, loopTimeInSeconds);
#else
    float y_movement = mod(iTime * 0.5, loopTimeInSeconds);
#endif
    float x_movement = y_movement * 0.25;
    for (int cell_index = 0, x; cell_index < cells.length(); cell_index++)
    {
        Cell cell = cells[cell_index];
        for (x = 0; x < 6; x++)
        {
            vec3 cell_hsv = GetHSVFromRGB(cell.color.rgb);
            vec4 cell_color = vec4(GetRGBFromHSV(vec3(cell_hsv.x + hue + cellHueOffset, cell_hsv.y, cell_hsv.z)), cell.color.a);
            color = AlphaBlendColors(color, DrawCell(vec2((uv.x + x_movement * cell.movementSpeedMultiplier) * minimalGridAxisCellCount + (4.0 - float(cell_index) * 2.0) + 0.75 * 0.5 - (minimalGridAxisCellCount * 0.25) * float(x), (uv.y + y_movement * cell.movementSpeedMultiplier) * minimalGridAxisCellCount + (-1.5 + float(cell_index)) - minimalGridAxisCellCount * float(x)), cell_color, cellGlowSize, cellGlowBoost));
        }
    }
    fragmentColor = vec4(GetNormalizedRGB(color.rgb), 1.0);
}

