#define TAU 6.283
#define pow(a,b) pow(abs(a),b)

vec2 f(vec2 p)
{
    vec2 t = vec2(pow(p.x,2.)-pow(p.y,2.), -2.*p.x*p.y);
    t *= sin(iTime*1.)*1. + 1.05;
    float b = pow(p.x,4.) + pow(p.y,4.) + 2.*pow(p.x,2.)*pow(p.y,2.);
    return (t/b)-(p/3.);
}

vec2 P(vec2 p, float i)
{
    vec2 v = f(f(f(f(f(f(p))))));
    float ti = TAU * (i/3.0);

    #ifdef SHIFT
    ti -= iTime - p.x * p.x * length(p);
    #endif

    return v - vec2(cos(ti),sin(ti));    
}


