    vec2 rdxz = normalize(rd.xz);
    float t = -dot(rdxz, co.xz);
    vec2 rp = co.xz + rdxz * t;
    float k = c.size.x*c.size.x - dot(rp,rp);

    float lxz = length(rd.xz);

    // body near
    n = t - sqrt(k);
    n = n/lxz;

    // body far
    f = t + sqrt(k);
    f = f/lxz;

    // no intersection
    if(k < 0. || f < 0. || f < n)
        return -1.;

    // y intersect position
    float nph = rd.y * n + co.y;
    float fph = rd.y * f + co.y;

    // cap intersection
    float ct = ( c.size.y - co.y)/rd.y;
    float cb = (-c.size.y - co.y)/rd.y;
    if(ct < 0.)ct = MAX;
    if(cb < 0.)cb = MAX;

    // far
    if(abs(fph) <= c.size.y && (n < 0. || abs(nph) > c.size.y))
        h = f;

    // cap
    if(fph <  c.size.y &&  c.size.y < nph) h = ct;
    if(fph > -c.size.y && -c.size.y > nph) h = cb;
    if(n < 0. && abs(co.y) < c.size.y)
        h = min(min(ct,cb), f); // inside intersect

    // near
    if(abs(nph) <= c.size.y && n >= 0.)
        h = n;

    return h;
}

vec3 nCylinder(vec3 rp, Cylinder c){
    vec3 cp = rp - c.pos;
    vec3 n = vec3(0);
    if(abs(cp.y)+0.0001 >= c.size.y) n = vec3(0,1,0)*(cp.y/abs(cp.y));
    else n.xz = normalize(cp.xz);
    return n;
}

