float f_n_sin_interpolated(
    float n_t
){
    float n_tau = 6.2831;
    float a = -1.*cos(n_t*n_tau*.25);
    float b = cos((n_t-1.)*n_tau*.25);
    float c = n_t*b + (1.-n_t)*a;
    return c;
}
float f_n_exp_interpolated(
    float n_t 
){
        float a = n_t*n_t;
        float b = pow(n_t-1.,2.);
        float c = n_t*b + (1.-n_t)*a; 
        return c;

}
